// Funci贸n para calcular el horario de fin sumando 30 minutos
function calcularHoraFin(horaInicio) {
  const [horas, minutos] = horaInicio.split(':').map(num => parseInt(num, 10));
  let nuevasHoras = horas;
  let nuevosMinutos = minutos + 30;

  if (nuevosMinutos >= 60) {
    nuevasHoras += 1;
    nuevosMinutos -= 60;
  }

  return `${nuevasHoras.toString().padStart(2, '0')}:${nuevosMinutos.toString().padStart(2, '0')}`;
}

