const eventosVerdes = [];
const eventosRojos = [];

for (const item of eventos) {
  const ev = item.json;
  const start = ev.start?.dateTime || ev.start?.date || ev.start;
  const end = ev.end?.dateTime || ev.end?.date || ev.end;

  if (!start || !end) continue;

  const summary = ev.summary?.toLowerCase() || "";
  const location = ev.location?.toLowerCase() || "";
  const description = ev.description?.toLowerCase() || "";
  const colorId = ev.colorId?.toString();

  const coincide = summary.includes(codigoBusqueda) || location.includes(codigoBusqueda) || description.includes(codigoBusqueda);

  if (!coincide) continue;

  if (colorId === "2") {
    eventosVerdes.push({ start, end, summary, location });
  } else if (colorId === "11") {
    eventosRojos.push({ start, end });
  }
}

//  Si no hay verdes, devolver mensaje
if (eventosVerdes.length === 0) {
  return [{
    json: {
      noMatchingEvents: true,
      message: "No se encontraron eventos disponibles para la propiedad."
    }
  }];
}

//  Procesar horarios disponibles
const resultados = eventosVerdes.map(evento => {
  const inicioVerdeMin = extraerMinutos(evento.start);
  const finVerdeMin = extraerMinutos(evento.end);

  const bloques = [];

  for (let m = inicioVerdeMin; m + 30 <= finVerdeMin; m += 30) {
    let bloqueOk = true;

    for (const rojo of eventosRojos) {
      const inicioRojoMin = extraerMinutos(rojo.start);
      const finRojoMin = extraerMinutos(rojo.end);

      if (inicioRojoMin === null || finRojoMin === null) continue;

      // Verificar solapamiento
      if (!(m + 30 <= inicioRojoMin || m >= finRojoMin)) {
        bloqueOk = false;
        break;
      }
    }

    if (bloqueOk) {
      const horaInicio = minutosAHora(m);
      const horaFin = minutosAHora(m + 30);

      bloques.push({
        horaInicio: horaInicio,
        horaFin: horaFin,
        inicio: crearFechaISO(evento.start, horaInicio),
        fin: crearFechaISO(evento.start, horaFin)
      });
    }
  }

  return {
    eventoPrincipal: {
      resumen: evento.summary,
      location: evento.location,
      inicio: evento.start,
      fin: evento.end,
      horaInicio: minutosAHora(inicioVerdeMin),
      horaFin: minutosAHora(finVerdeMin)
    },
    horariosDisponibles: bloques
  };
});

//  Devolver la respuesta
return [{
  json: {
    resultados: resultados
  }
}];
