// Versión con botones completamente dinámicos

// Función para generar botones dinámicamente
function generateButtons(buttonsData) {
  // Si vienen botones personalizados
  if ($json.buttons && Array.isArray($json.buttons)) {
    return $json.buttons.map((button, index) => ({
      type: "quick_reply",
      title: button.title || button.text || `Botón ${index + 1}`,
      id: button.id || String(index + 1)
    }));
  }

  // Si no, usar los botones por defecto
  return [
    {
      type: "quick_reply",
      title: $json.button1Text || 'Confirmar',
      id: "1"
    },
    {
      type: "quick_reply",
      title: $json.button2Text || 'Cancelar',
      id: "2"
    }
  ];
}

const messageData = {
  header: {
    text: $json.headerText
  },
  body: {
    text:$input.first().json.body
  },
  footer: {
    text: $json.footerText || ""
  },
  action: {
    buttons: generateButtons()
  },
  type: "button",
  to: $input.first().json.telefono
};

return messageData;
