// Obtener el timestamp actual como string (formato: "2025-04-30T15:11")
const timestampStr = $input.first().json.timestamp;
// Separar la fecha y la hora
const [fecha, hora] = timestampStr.split('T');
let [horas, minutos] = hora.split(':').map(num => parseInt(num, 10));

// Sumar 1 hora al timestamp original (según el comentario)
horas = (horas + 1) % 24;
const horasStr = String(horas).padStart(2, '0');
const minutosStr = String(minutos).padStart(2, '0');
const timestampAjustado = `${fecha}T${horasStr}:${minutosStr}`;

// Calcular horas y minutos para +5 minutos (partiendo de la hora ya ajustada)
let horasPlus = horas;
let minutosPlus = minutos + 5;
if (minutosPlus >= 60) {
  minutosPlus = minutosPlus - 60;
  horasPlus = (horasPlus + 1) % 24;
}

// Calcular horas y minutos para -5 minutos (partiendo de la hora ya ajustada)
let horasMinus = horas;
let minutosMinus = minutos - 5;
if (minutosMinus < 0) {
  minutosMinus = minutosMinus + 60;
  horasMinus = (horasMinus - 1 + 24) % 24;
}

// Formatear de vuelta a strings
const horasPlus_str = String(horasPlus).padStart(2, '0');
const minutosPlus_str = String(minutosPlus).padStart(2, '0');
const timestamp_plus5 = `${fecha}T${horasPlus_str}:${minutosPlus_str}`;

const horasMinus_str = String(horasMinus).padStart(2, '0');
const minutosMinus_str = String(minutosMinus).padStart(2, '0');
const timestamp_minus5 = `${fecha}T${horasMinus_str}:${minutosMinus_str}`;

// Devolver los resultados
return {
  json: {
    ...($input.first().json),
    timestamp_original: timestampStr,
    timestamp_ajustado: timestampAjustado,  // Nueva propiedad con +1h
    timestamp_minus5: timestamp_minus5,     // -5m desde la hora ajustada
    timestamp_plus5: timestamp_plus5        // +5m desde la hora ajustada
  }
};

