}
mensaje += `📅 *Fecha límite: ${fechaLimiteFormateada}*\n\n`;

// Define fechaUltimoPago first, then set the formatted version
let fechaUltimoPago = $('Obtener Citas Para Recordatorios').first().json.fecha_ultimo_pago;
