// Función para formatear texto
function formatText(input) {
    const replacements = {
        primera: "1°",
        "la primera": "1°",
        segunda: "2°",
        "la segunda": "2°",
        tercera: "3°",
        "la tercera": "3°",
        cuarta: "4°",
        "la cuarta": "4°",
        quinta: "5°",
        "la quinta": "5°",
        "primer semana de": "1° quincena",
        "una semana de": "1° quincena",
        "la primer semana de": "1° quincena",
        "segunda semana de": "2° quincena",
        "la segunda semana de": "2° quincena",
        "quincena de": "quincena",
        "para la primera": "1°",
        "para la segunda": "2°",
        "para la": "",
        "para el": "",
        "de": "",
        "la": "",
        "el": "",
        "en": "",
        "semana de": "semana"
    };

    let formattedText = input.toLowerCase().trim();

    // Aplicar reemplazos
    for (const [key, value] of Object.entries(replacements)) {
        const regex = new RegExp(`\\b${key}\\b`, "g");
        formattedText = formattedText.replace(regex, value);
    }

    // Eliminar espacios sobrantes
    formattedText = formattedText.replace(/\s+/g, " ").trim();

    // Convertir a formato esperado (capitalizar)
    formattedText = formattedText
        .split(" ")
        .map(word => word.charAt(0).toLowerCase() + word.slice(1))
        .join(" ");

    return formattedText;
}

// Procesar datos de entrada
const inputData = $input.all(); // Obtener todos los elementos de entrada

if (!inputData || inputData.length === 0) {
    throw new Error("No se recibieron datos de entrada. Verifica el nodo anterior.");
}

const outputData = inputData.map(item => {
    // Validar que los campos necesarios estén presentes
    const query = item.json.query;
    if (!query) {
        throw new Error("El campo 'query' no está definido en los datos de entrada.");
    }
    if (!query.periodo) {
        throw new Error("El campo 'periodo' no está definido en 'query'.");
    }
    if (!query.precioMin || !query.precioMax) {
        throw new Error("Faltan los campos 'precioMin' o 'precioMax' en 'query'.");
    }

    // Formatear el periodo
    const formattedPeriodo = formatText(query.periodo);

    // Validar valores de precio
    if (isNaN(query.precioMin) || isNaN(query.precioMax)) {
        throw new Error("Los valores de 'precioMin' y 'precioMax' deben ser números.");
    }

    // Devolver los datos formateados para el siguiente nodo
    return {
        json: {
            ...item.json, // Mantener todos los datos originales
            query: {
                ...query,
                formattedPeriodo // Incluir el periodo formateado
            }
        }
    };
});

// Devolver los datos procesados
return outputData;
