    // Si el bloque no se solapa con ningÃºn evento rojo, aÃ±adirlo a los disponibles
    if (bloqueDisponible) {
      const horaInicio = minutosAHora(m);
      const horaFin = minutosAHora(m + 30);
      bloques.push({
        horaInicio: horaInicio,
        horaFin: horaFin,
        inicio: crearFechaISO(evento.start, horaInicio),
        fin: crearFechaISO(evento.start, horaFin)
      });
    }
  }

  return {
    eventoPrincipal: {
      resumen: evento.summary,
      location: evento.location,
      inicio: evento.start,
      fin: evento.end,
      horaInicio: minutosAHora(inicioVerdeMin),
      horaFin: minutosAHora(finVerdeMin)
    },
