// Obtenemos el evento verde principal
const eventoVerde = $input.first().json;
console.log("Evento verde principal:", eventoVerde.summary);

// Obtenemos fechas exactamente como vienen en el formato original
const fechaInicioStr = eventoVerde.start.dateTime;
const fechaFinStr = eventoVerde.end.dateTime;

// Extraemos solo las partes de hora del string ISO
const horaInicioVerde = fechaInicioStr.substring(11, 16); // Formato HH:MM
const horaFinVerde = fechaFinStr.substring(11, 16);       // Formato HH:MM

console.log(`Horario original evento verde: ${horaInicioVerde} a ${horaFinVerde}`);

// Funci√≥n para extraer las horas y minutos exactos de un string ISO
function extraerHoraMinutos(isoString) {
  const horas = parseInt(isoString.substring(11, 13), 10);
  const minutos = parseInt(isoString.substring(14, 16), 10);
  return { horas, minutos, total: horas * 60 + minutos };
}

// Funci√≥n para convertir total de minutos a formato HH:MM
function minutosAHora(totalMinutos) {
  const horas = Math.floor(totalMinutos / 60);
  const minutos = totalMinutos % 60;
  return {
    horas, 
    minutos,
    formato: `${horas.toString().padStart(2, '0')}:${minutos.toString().padStart(2, '0')}`
  };
}

// Obtenemos el total de minutos para el inicio y fin del evento verde
const inicioVerdeMinutos = extraerHoraMinutos(fechaInicioStr).total;
const finVerdeMinutos = extraerHoraMinutos(fechaFinStr).total;

console.log(`Minutos inicio: ${inicioVerdeMinutos}, Minutos fin: ${finVerdeMinutos}`);

// Generamos los bloques de 30 minutos dentro del rango del evento verde
const bloques = [];
for (let minutos = inicioVerdeMinutos; minutos < finVerdeMinutos; minutos += 30) {
  // Solo agregamos si el bloque completo cabe en el evento verde
  if (minutos + 30 <= finVerdeMinutos) {
    const horaInicio = minutosAHora(minutos);
    const horaFin = minutosAHora(minutos + 30);

    bloques.push({
      inicioMinutos: minutos,
      finMinutos: minutos + 30,
      horaInicio: horaInicio.formato,
      horaFin: horaFin.formato
    });
  }
}

console.log(`Total de bloques generados: ${bloques.length}`);
bloques.forEach(b => console.log(`Bloque: ${b.horaInicio} a ${b.horaFin}`));

// Obtenemos todos los eventos para buscar los rojos
const eventos = $input.all();
const eventosRojos = [];

// Buscamos eventos rojos
for (const item of eventos) {
  const evento = item.json;
  if (evento.colorId === "11" || evento.colorId === 11) {
    const inicioRojoStr = evento.start.dateTime;
    const finRojoStr = evento.end.dateTime;

    // Convertimos las horas a minutos para facilitar comparaci√≥n
    const inicioRojoMinutos = extraerHoraMinutos(inicioRojoStr).total;
    const finRojoMinutos = extraerHoraMinutos(finRojoStr).total;

    eventosRojos.push({
      inicioMinutos: inicioRojoMinutos,
      finMinutos: finRojoMinutos,
      horaInicio: inicioRojoStr.substring(11, 16),
      horaFin: finRojoStr.substring(11, 16)
    });

    console.log(`Evento rojo: ${inicioRojoStr.substring(11, 16)} a ${finRojoStr.substring(11, 16)}`);
  }
}

// Verificamos cada bloque contra los eventos rojos
const bloquesDisponibles = [];

for (const bloque of bloques) {
  let disponible = true;

  for (const rojo of eventosRojos) {
    // Verificar solapamiento usando los minutos
    if (bloque.inicioMinutos < rojo.finMinutos && rojo.inicioMinutos < bloque.finMinutos) {
      disponible = false;
      console.log(`Bloque ${bloque.horaInicio}-${bloque.horaFin} est√° ocupado por ${rojo.horaInicio}-${rojo.horaFin}`);
      break;
    }
  }

  if (disponible) {
    bloquesDisponibles.push(bloque);
    console.log(`Bloque disponible: ${bloque.horaInicio}-${bloque.horaFin}`);
  }
}

// Formateamos las fechas ISO para los bloques disponibles
// Esto mantiene el formato ISO pero actualiza solo la parte de hora
function crearFechaISOConHora(fechaBase, hora, minuto) {
  // Tomamos la fecha base y solo cambiamos las horas y minutos
  return fechaBase.substring(0, 11) + 
         hora.toString().padStart(2, '0') + ":" + 
         minuto.toString().padStart(2, '0') + 
         fechaBase.substring(16);
}

// Resultado final
const horariosDisponibles = bloquesDisponibles.map(bloque => {
  const horaInicio = parseInt(bloque.horaInicio.split(':')[0], 10);
  const minInicio = parseInt(bloque.horaInicio.split(':')[1], 10);
  const horaFin = parseInt(bloque.horaFin.split(':')[0], 10);
  const minFin = parseInt(bloque.horaFin.split(':')[1], 10);

  return {
    inicio: crearFechaISOConHora(fechaInicioStr, horaInicio, minInicio),
    fin: crearFechaISOConHora(fechaInicioStr, horaFin, minFin), 
    horaInicio: bloque.horaInicio,
    horaFin: bloque.horaFin
  };
});

return [{
  json: {
    eventoPrincipal: {
      resumen: eventoVerde.summary,
      inicio: fechaInicioStr,
      fin: fechaFinStr,
      horaInicio: horaInicioVerde,
      horaFin: horaFinVerde
    },
    horariosDisponibles: horariosDisponibles
  }
}];
