// Funci√≥n para formatear el rango de hora correctamente
function formatearRangoHora(horaInicio, horaFin) {
  const inicioMatch = horaInicio.match(/^(\d+):(\d+)/);
  const finMatch = horaFin.match(/^(\d+):(\d+)/);

  const horaInicioFormateada = inicioMatch ? `${inicioMatch[1]}:${inicioMatch[2]}` : horaInicio;
  const horaFinFormateada = finMatch ? `${finMatch[1]}:${finMatch[2]}` : horaFin;

  return `${horaInicioFormateada} a ${horaFinFormateada}`;
}

// Funci√≥n para crear fecha en formato ISO con timezone de Argentina
function crearFechaISO(anio, mes, dia, hora) {
  // Parsear la hora (formato "HH:MM")
  const [horas, minutos] = hora.split(':').map(Number);

  // Crear fecha en timezone de Argentina
  const fecha = new Date(anio, mes - 1, dia, horas, minutos, 0);

  // Formatear en ISO simple sin prefijos
  const year = fecha.getFullYear();
  const month = String(fecha.getMonth() + 1).padStart(2, '0');
  const day = String(fecha.getDate()).padStart(2, '0');
  const hour = String(fecha.getHours()).padStart(2, '0');
  const minute = String(fecha.getMinutes()).padStart(2, '0');

  // Retornar formato ISO limpio
  return `${year}-${month}-${day}T${hour}:${minute}:00.000-03:00`;
