const eventosDisponibles = [];

for (const item of eventos) {
    const ev = item.json;
    const start = ev.start?.dateTime || ev.start?.date || ev.start;
    const end = ev.end?.dateTime || ev.end?.date || ev.end;

    // Filtrar elementos que no tienen fecha v√°lida
    if (!start || !end || 
        start === "America/Argentina/Buenos_Aires" || 
        end === "America/Argentina/Buenos_Aires") {
        console.log("‚è≠Ô∏è Saltando evento sin fechas v√°lidas");
        continue;
    }

    const summary = ev.summary || "";

    // Buscar si summary contiene "Codigo:XXXXX"
    const summaryUpper = summary.toUpperCase();
    const patronBusqueda = "CODIGO:" + codigoBusqueda;
    const coincide = summaryUpper.includes(patronBusqueda);

    console.log(`üìÖ Evento: "${summary}"`);
    console.log(`üîç Buscando: "${patronBusqueda}"`);
    console.log(`‚úÖ ¬øCoincide? ${coincide}`);

    if (coincide) {
        // Validar que las fechas se puedan parsear
        const fechaInicio = new Date(start);
        const fechaFin = new Date(end);

        if (isNaN(fechaInicio.getTime()) || isNaN(fechaFin.getTime())) {
            console.log("‚ùå Fechas inv√°lidas en evento:", start, end);
            continue;
        }

        eventosDisponibles.push({ 
            start, 
            end, 
            summary, 
            location: ev.location || "",
            colorId: "10",
            fechaInicioParsed: fechaInicio.toISOString(),
            fechaFinParsed: fechaFin.toISOString()
        });

        console.log("‚úÖ Evento agregado a disponibles");
    }
}

console.log(`\nüìä Total eventos disponibles encontrados: ${eventosDisponibles.length}`);

// üöÄ Procesar eventos rojos (visitas agendadas) para exclusi√≥n
console.log("\n--- PROCESANDO EVENTOS ROJOS (OCUPADOS) ---");
const bloquesOcupados = [];

// Validar que hay eventos rojos para procesar
if (contadorEventosRojos === 0) {
    console.log("‚ÑπÔ∏è No hay eventos rojos (colorId = 11) para procesar. Todos los horarios estar√°n disponibles.");
} else {
    console.log(`‚úÖ ${contadorEventosRojos} eventos rojos encontrados, procesando exclusiones...`);

    // Procesar cada evento rojo encontrado
    eventosRojosData.forEach((eventoRojo, index) => {
        console.log(`\nüî¥ Procesando evento rojo ${index + 1}:`);
        console.log("ColorId:", eventoRojo.colorId);
        console.log("Summary:", eventoRojo.summary || "Sin t√≠tulo");
        console.log("Evento completo:", JSON.stringify(eventoRojo, null, 2));

        // Extraer fechas del evento rojo
        let fechaInicioRojo, fechaFinRojo;

        if (eventoRojo.start && eventoRojo.start.dateTime) {
            fechaInicioRojo = eventoRojo.start.dateTime;
        } else if (eventoRojo.start) {
            fechaInicioRojo = eventoRojo.start;
        }

        if (eventoRojo.end && eventoRojo.end.dateTime) {
            fechaFinRojo = eventoRojo.end.dateTime;
        } else if (eventoRojo.end) {
            fechaFinRojo = eventoRojo.end;
        }

        if (fechaInicioRojo && fechaFinRojo) {
            const inicioMin = extraerMinutos(fechaInicioRojo);
            const finMin = extraerMinutos(fechaFinRojo);

            if (inicioMin !== null && finMin !== null) {
                // Extraer fecha base (YYYY-MM-DD) para comparar con eventos disponibles
                const fechaBaseMatch = fechaInicioRojo.match(/^(\d{4}-\d{2}-\d{2})/);
                const fechaBase = fechaBaseMatch ? fechaBaseMatch[1] : null;

                const bloqueOcupado = {
                    fechaBase: fechaBase,
                    inicioMin: inicioMin,
                    finMin: finMin,
                    fechaInicioCompleta: fechaInicioRojo,
                    fechaFinCompleta: fechaFinRojo,
                    summary: eventoRojo.summary || "Visita agendada",
                    colorId: eventoRojo.colorId
                };

                bloquesOcupados.push(bloqueOcupado);

                console.log(`‚úÖ Bloque ocupado: ${fechaBase} de ${minutosAHora(inicioMin)} a ${minutosAHora(finMin)} (${eventoRojo.summary || 'Sin t√≠tulo'})`);
            } else {
                console.log("‚ùå No se pudieron extraer minutos del evento rojo");
            }
        } else {
            console.log("‚ùå No se pudieron extraer fechas del evento rojo");
        }
    });
}

console.log(`\nüìã Total bloques ocupados: ${bloquesOcupados.length}`);

// Funci√≥n para verificar si un bloque est√° ocupado
const estaOcupado = (fechaBase, inicioMin, finMin) => {
    return bloquesOcupados.some(ocupado => {
        // Debe ser la misma fecha
        if (ocupado.fechaBase !== fechaBase) return false;

        // Verificar superposici√≥n de tiempo:
        // Un bloque est√° ocupado si hay ANY superposici√≥n con un evento rojo
        const haySuperpocision = (
            // El nuevo bloque empieza antes de que termine el ocupado Y
            inicioMin < ocupado.finMin &&
            // El nuevo bloque termina despu√©s de que empiece el ocupado
            finMin > ocupado.inicioMin
        );

        if (haySuperpocision) {
            console.log(`‚ùå Bloque ${minutosAHora(inicioMin)}-${minutosAHora(finMin)} ocupado por: ${ocupado.summary}`);
        }

        return haySuperpocision;
    });
};

// üöÄ Si no hay eventos disponibles
if (eventosDisponibles.length === 0) {
    return [{
        json: {
            noMatchingEvents: true,
            message: `No se encontraron eventos disponibles para el c√≥digo: ${codigoBusqueda}`,
            debug: {
                codigoBuscado: codigoBusqueda,
                patronBusqueda: "CODIGO:" + codigoBusqueda,
                totalEventosAnalizados: eventos.length
            }
        }
    }];
}

// üöÄ Procesar horarios disponibles con tiempo din√°mico
console.log(`\n--- GENERANDO BLOQUES DE ${tiempoReservaMin} MINUTOS ---`);

const resultados = eventosDisponibles.map((evento, index) => {
    console.log(`\nüèóÔ∏è Procesando evento ${index + 1}: "${evento.summary}"`);

    const inicioEventoMin = extraerMinutos(evento.start);
    const finEventoMin = extraerMinutos(evento.end);

    if (inicioEventoMin === null || finEventoMin === null) {
        console.log("‚ùå No se pudieron extraer minutos del evento");
        return {
            eventoPrincipal: {
                resumen: evento.summary,
                location: evento.location,
                inicio: evento.start,
                fin: evento.end,
                colorId: "10",
                error: "No se pudieron procesar las horas"
            },
            horariosDisponibles: []
        };
    }

    console.log(`‚è∞ Evento de ${minutosAHora(inicioEventoMin)} a ${minutosAHora(finEventoMin)}`);
    console.log(`üìè Duraci√≥n total: ${finEventoMin - inicioEventoMin} minutos`);

    // Crear bloques basados en el tiempo de reserva
    const bloques = [];
    let bloquesGenerados = 0;
    let bloquesExcluidos = 0;

    // Extraer fecha base del evento para comparar con eventos rojos
    const fechaBaseEvento = evento.start.match(/^(\d{4}-\d{2}-\d{2})/);
    const fechaBase = fechaBaseEvento ? fechaBaseEvento[1] : null;

    for (let m = inicioEventoMin; m + tiempoReservaMin <= finEventoMin; m += tiempoReservaMin) {
        const horaInicio = minutosAHora(m);
        const horaFin = minutosAHora(m + tiempoReservaMin);

        // ‚≠ê VERIFICAR SI ESTE BLOQUE EST√Å OCUPADO
        const bloqueOcupado = estaOcupado(fechaBase, m, m + tiempoReservaMin);

        if (bloqueOcupado) {
            bloquesExcluidos++;
            console.log(`  ‚ùå Bloque ${horaInicio} - ${horaFin} EXCLUIDO (ocupado)`);
            continue; // Saltar este bloque
        }

        const bloque = {
            horaInicio: horaInicio,
            horaFin: horaFin,
            inicio: crearFechaISO(evento.start, horaInicio),
            fin: crearFechaISO(evento.start, horaFin),
            duracionMinutos: tiempoReservaMin
        };

        bloques.push(bloque);
        bloquesGenerados++;

        console.log(`  ‚úÖ Bloque ${bloquesGenerados}: ${horaInicio} - ${horaFin} DISPONIBLE`);
    }

    console.log(`‚úÖ Total bloques disponibles: ${bloquesGenerados}`);
    console.log(`‚ùå Total bloques excluidos: ${bloquesExcluidos}`);

    return {
        eventoPrincipal: {
            resumen: evento.summary,
            location: evento.location,
            inicio: evento.start,
            fin: evento.end,
            horaInicio: minutosAHora(inicioEventoMin),
            horaFin: minutosAHora(finEventoMin),
            colorId: "10",
            duracionTotalMinutos: finEventoMin - inicioEventoMin,
            tiempoReservaMinutos: tiempoReservaMin
        },
        horariosDisponibles: bloques,
        estadisticas: {
            totalBloques: bloquesGenerados,
            bloquesExcluidos: bloquesExcluidos,
            duracionBloque: tiempoReservaMin,
            duracionEvento: finEventoMin - inicioEventoMin
        }
    };
});

// üöÄ Calcular estad√≠sticas generales
const totalBloques = resultados.reduce((sum, resultado) => sum + resultado.horariosDisponibles.length, 0);
const totalExcluidos = resultados.reduce((sum, resultado) => sum + (resultado.estadisticas?.bloquesExcluidos || 0), 0);

console.log(`\nüìà RESUMEN FINAL:`);
console.log(`- Eventos disponibles: ${eventosDisponibles.length}`);
console.log(`- Total bloques de tiempo DISPONIBLES: ${totalBloques}`);
console.log(`- Total bloques EXCLUIDOS (ocupados): ${totalExcluidos}`);
console.log(`- Duraci√≥n por bloque: ${tiempoReservaMin} minutos`);
console.log(`- Eventos rojos (colorId=11) encontrados: ${contadorEventosRojos}`);

// üöÄ Devolver la respuesta
return [{
    json: {
        resultados: resultados,
        resumen: {
            codigoBuscado: codigoBusqueda,
            tiempoReservaMinutos: tiempoReservaMin,
            eventosEncontrados: eventosDisponibles.length,
            totalBloquesDisponibles: totalBloques,
            totalBloquesExcluidos: totalExcluidos,
            eventosRojosEncontrados: contadorEventosRojos
        }
    }
}];
