// Obtener historial de mensajes desde Redis
const redisMessages = $input.first()?.json?.Redis1 || [];

// Extraer el último mensaje recibido, asegurando que sea un string válido
const userMessage = Array.isArray(redisMessages) && redisMessages.length > 0
  ? String(redisMessages[redisMessages.length - 1]).trim().toLowerCase()
  : "";

// Obtener la cantidad de mensajes en cola desde Redis en los últimos 6 segundos
const queueLength = parseInt($input.first()?.json?.queueLength || "0", 10);

// Contar palabras en el mensaje
const words = userMessage.trim().split(/\s+/).filter(Boolean);
const wordCount = words.length;

// Contar caracteres en el mensaje
