// Función que extrae información cuando un usuario selecciona una fecha (desde el ListResponseMessage)
const procesarSeleccionDeFecha = () => {
  try {
    // Verificamos si es una respuesta a una lista (selección de fecha)
    const webhookData = $('Webhook').first().json;
    if (webhookData && webhookData.body && webhookData.body.data && 
        webhookData.body.data.message && webhookData.body.data.message.listResponseMessage) {

      const listResponse = webhookData.body.data.message.listResponseMessage;
      if (listResponse.singleSelectReply && listResponse.singleSelectReply.selectedRowId) {
        const selectedId = listResponse.singleSelectReply.selectedRowId.trim();

        // Formato esperado: mes-dia-hora (ej: 5-8-1600)
        const partes = selectedId.split('-');
        if (partes.length >= 3) {
          const now = new Date();
          const anioActual = now.getFullYear();
          const mesNumero = partes[0];
          const dia = partes[1];

          // Formatea la hora (ej: "1600" a "16:00")
          let horarioRaw = partes[2].toString().padStart(4, '0');
          const hora = horarioRaw.slice(0, 2);
          const minutos = horarioRaw.slice(2, 4);
          const horarioFormateado = `${hora}:${minutos}`;

          // Construye la fecha ISO para la base de datos
          const fechaISO = `${anioActual}-${mesNumero.padStart(2, '0')}-${dia.padStart(2, '0')}T${horarioFormateado}:00`;

          // Nombres de meses para mostrar en mensajes
