            tipo: "seleccion_fecha",
            detalles: {
              dia: dia,
              mes: mesNumero,
              mes_nombre: nombresMeses[mesNumero],
              hora: horarioFormateado,
              fecha_iso: fechaISO,
              texto_legible: `${dia} de ${nombresMeses[mesNumero]} a las ${horarioFormateado}`
            }
          };
        }
      }
    }

    // Verificamos si es una confirmación de datos
    const mensajeTexto = webhookData?.body?.data?.message?.conversation?.toLowerCase();
    if (mensajeTexto && (mensajeTexto.includes("son correctos") || mensajeTexto.includes("si son") || mensajeTexto === "si")) {
