    if (!item || !item.json) return;

    const event = item.json;

    // Ignorar objetos que son solo timezone
    if (event.start === "America/Argentina/Buenos_Aires" && 
        event.end === "America/Argentina/Buenos_Aires") {
      return;
    }

    // Filtrar solo eventos ROJOS (colorId=11)
    if (event.colorId === "11" || event.colorId === 11) {
      let coincideEmail = false;

      // Verificar si el evento tiene attendees (asistentes)
      if (event.attendees && Array.isArray(event.attendees)) {
        // Buscar si alguno de los asistentes tiene el email buscado
        coincideEmail = event.attendees.some(attendee => 
          attendee && attendee.email && 
          attendee.email.toLowerCase() === emailBuscadoLowerCase
        );
      }

      // Si no coincide por attendees, buscar en todo el evento
      if (!coincideEmail) {
        try {
          const eventString = JSON.stringify(event).toLowerCase();
          coincideEmail = eventString.includes(emailBuscadoLowerCase);
        } catch (error) {
          console.log(`Error al procesar evento: ${error.message}`);
        }
      }

      // Si encontramos coincidencia, agregar a la lista
      if (coincideEmail) {
        console.log(`Evento ROJO con email coincidente: ${event.summary || 'Sin t√≠tulo'}`);
        eventosRojosFiltrados.push({
          ...event,
          tipo: "rojo"
        });
      }
    }
  });

  // Si no encontramos eventos con el email, devolver todos los eventos rojos
  if (eventosRojosFiltrados.length === 0) {
    console.log("\n=== NO SE ENCONTRARON COINCIDENCIAS. DEVOLVIENDO TODOS LOS EVENTOS ROJOS ===");

    events.forEach(item => {
      if (!item || !item.json) return;

      const event = item.json;

      // Ignorar objetos timezone
      if (event.start === "America/Argentina/Buenos_Aires" && 
          event.end === "America/Argentina/Buenos_Aires") {
