function extraerCodigoPropiedad(evento) {
  let codigo = "Sin cÃ³digo";

  if (evento.description) {
    const matchDescripcion = evento.description.match(/[Cc][oÃ³]digo\s*(?:propiedad|de\s*propiedad)?:\s*([A-Za-z0-9-_]+)/i);
    if (matchDescripcion) {
      codigo = matchDescripcion[1];
    }
  }

  if (codigo === "Sin cÃ³digo" && evento.summary) {
    const matchSummary = evento.summary.match(/[Cc][oÃ³]digo:\s*([A-Za-z0-9-_]+)/i);
    if (matchSummary) {
      codigo = matchSummary[1];
    }
  }

  return codigo;
}

