// Obtener el código de propiedad que queremos buscar
const codigoPropiedad = $('Code').first().json.ultimoCodigo;

// Procesar todos los eventos de entrada
const events = $input.all();

// Para debugging
console.log("Número total de eventos a procesar:", events.length);
console.log("Código de propiedad a buscar:", codigoPropiedad);

// Arrays para almacenar los eventos filtrados
const eventosVerdes = [];
const eventosRojos = [];

// Mapa para almacenar las relaciones entre ubicaciones y códigos
// Esto nos ayudará a relacionar eventos rojos con códigos de propiedad
const relacionUbicacionCodigo = new Map();

// PASO 1: Escanear eventos verdes para recopilar relaciones ubicación->código
console.log("\n=== FASE 1: ESCANEO DE UBICACIONES Y CÓDIGOS ===");
events.forEach(item => {
  const event = item.json;

  // Ignorar objetos que son solo timezone
  if (event.start === "America/Argentina/Buenos_Aires" && 
      event.end === "America/Argentina/Buenos_Aires") {
    return;
  }

  // Detectar eventos verdes con "Codigo:XXX"
  if ((event.colorId === "10" || event.colorId === 10) && 
      event.summary && 
      event.summary.includes("Codigo:") && 
      event.location) {

    // Extraer el código de la propiedad
    const codigoMatch = event.summary.match(/Codigo:([A-Za-z0-9-_]+)/i);
    if (codigoMatch) {
      const codigo = codigoMatch[1];
      // Guardar la relación ubicación -> código
      relacionUbicacionCodigo.set(event.location, codigo);
      console.log(`Ubicación ${event.location} asociada con código ${codigo}`);
    }
  }
});

// PASO 2: Procesar todos los eventos
console.log("\n=== FASE 2: PROCESAMIENTO DE EVENTOS ===");
events.forEach(item => {
  const event = item.json;

  // Ignorar objetos que son solo timezone
  if (event.start === "America/Argentina/Buenos_Aires" && 
      event.end === "America/Argentina/Buenos_Aires") {
    return;
  }

  // CASO 1: Eventos verdes (colorId=2)
  if (event.colorId === "10" || event.colorId === 10) {
    // Extraer código directamente del summary
    let codigoEvento = null;
    const codigoMatch = event.summary && event.summary.match(/Codigo:([A-Za-z0-9-_]+)/i);

    if (codigoMatch) {
      codigoEvento = codigoMatch[1];
    }

    // Verificar si el código coincide con el buscado
    if (codigoEvento === codigoPropiedad) {
      console.log(`Evento VERDE con código coincidente: ${event.summary}`);
      eventosVerdes.push({
        ...event,
        tipo: "verde"
      });
    }
  }
  // CASO 2: Eventos rojos (colorId=11)
  else if (event.colorId === "11" || event.colorId === 11) {
    // Para eventos rojos, verificar:
    // 1. Si tiene un formato "Direccion: XXX" y la ubicación coincide con un código conocido
    // 2. O si contiene directamente el código de propiedad

    let coincideRojo = false;

    // Verificar si su ubicación está asociada con el código buscado
    if (event.location && relacionUbicacionCodigo.has(event.location)) {
      const codigoAsociado = relacionUbicacionCodigo.get(event.location);
      if (codigoAsociado === codigoPropiedad) {
        coincideRojo = true;
        console.log(`Evento ROJO coincide por ubicación ${event.location}`);
      }
    }

    // También verificar si el summary contiene el código directamente
    if (event.summary && event.summary.includes(codigoPropiedad)) {
      coincideRojo = true;
      console.log(`Evento ROJO coincide por mención directa del código`);
    }

    if (coincideRojo) {
      eventosRojos.push({
        ...event,
        tipo: "rojo"
      });
    }
  }
});

// Si no encontramos eventos, hacer una segunda pasada más flexible
if (eventosVerdes.length === 0 && eventosRojos.length === 0) {
  console.log("\n=== NO SE ENCONTRARON EVENTOS EXACTOS, BUSCANDO POR UBICACIÓN ===");

  // Si hay eventos rojos pero no se relacionaron correctamente,
  // intentar buscar por coincidencia exacta de ubicación
  const eventosVerdesBuscados = events.filter(item => {
    const event = item.json;
    return (event.colorId === "10" || event.colorId === 10) && 
           event.summary && 
           event.summary.includes(`Codigo:${codigoPropiedad}`);
  }).map(item => item.json);

  // Si encontramos eventos verdes con el código buscado...
  if (eventosVerdesBuscados.length > 0) {
    console.log(`Encontrados ${eventosVerdesBuscados.length} eventos verdes con el código buscado`);

    // Extraer las ubicaciones de estos eventos verdes
    const ubicacionesBuscadas = eventosVerdesBuscados
      .filter(event => event.location)
      .map(event => event.location);

    console.log("Ubicaciones asociadas al código:", ubicacionesBuscadas);

    // Buscar eventos rojos con estas ubicaciones
    events.forEach(item => {
      const event = item.json;

      // Verificar si es un evento rojo con ubicación coincidente
      if ((event.colorId === "11" || event.colorId === 11) && 
          event.location && 
          ubicacionesBuscadas.includes(event.location)) {

        console.log(`Evento ROJO coincide por ubicación (segunda pasada): ${event.summary}`);
        eventosRojos.push({
          ...event,
          tipo: "rojo"
        });
      }
    });

    // Añadir los eventos verdes encontrados
    eventosVerdesBuscados.forEach(event => {
      eventosVerdes.push({
        ...event,
        tipo: "verde"
      });
    });
  }
}

// Si todavía no encontramos eventos, mostrar todos los eventos coloreados
if (eventosVerdes.length === 0 && eventosRojos.length === 0) {
  console.log("\n=== ÚLTIMO RECURSO: DEVOLVIENDO TODOS LOS EVENTOS COLOREADOS ===");

  // Capturar todos los eventos verdes (colorId=10)
  events.forEach(item => {
    const event = item.json;

    // Ignorar objetos timezone
    if (event.start === "America/Argentina/Buenos_Aires" && 
        event.end === "America/Argentina/Buenos_Aires") {
      return;
    }

    if (event.colorId === "10" || event.colorId === 10) {
      eventosVerdes.push({
        ...event,
        tipo: "verde"
      });
    }
    else if (event.colorId === "11" || event.colorId === 11) {
      eventosRojos.push({
        ...event,
        tipo: "rojo"
      });
    }
  });
}

// Registrar el resultado final
console.log(`\nResultado final: ${eventosVerdes.length} eventos verdes y ${eventosRojos.length} eventos rojos para el código ${codigoPropiedad}`);

// Devolver eventos encontrados
return [{
  json: {
    codigo: codigoPropiedad,
    eventosVerdes: eventosVerdes,
    eventosRojos: eventosRojos,
    totalEventos: eventosVerdes.length + eventosRojos.length
  }
}];

// Código para obtener el último código de propiedad
// Obtenemos todos los ítems de Redis
const redisItems =$input.first().json.Codigo_propiedad ;

// Verificamos si existe la propiedad y es un array
if (!Array.isArray(redisItems)) {
  console.log("Error: No se encontró un array de códigos de propiedad");
  return $input.all();
}

// Obtenemos el último elemento del array
const ultimoCodigo = redisItems[redisItems.length - 1];
console.log("Último código de propiedad:", ultimoCodigo);

// Añadimos el último código a todos los ítems de entrada
const items = $input.all();
for (const item of items) {
  item.json.ultimoCodigo = ultimoCodigo;
}

// Devolvemos los ítems con el nuevo campo
return items;

// 1. Obtener los datos
const datosCalendario = $('Calendario').first().json;

// Obtener datos de la propiedad (Google Sheets)
const datosPropiedad = $('Datos de la propiedad').first().json;

// Verificar que existen los datos necesarios en getForCode
let eventosVerdes = null;
try {
  // Intentar obtener el primer evento verde
  const getForCodeResult = $('getForCode').first().json;
  if (getForCodeResult && getForCodeResult.eventosVerdes && getForCodeResult.eventosVerdes.length > 0) {
    eventosVerdes = getForCodeResult.eventosVerdes[0];
  } else {
    console.log("No se encontraron eventos verdes");
  }
} catch (error) {
  console.log("Error al obtener eventos verdes:", error);
}

// 2. Extraer variables con valores predeterminados para evitar errores
const nombreCliente = datosCalendario?.Nombre || "";
const telefono = datosCalendario?.numero_cliente || "";
const nombreInmobiliaria = datosCalendario?.nombre_inmobiliaria || "";
const tipoPropiedad = datosCalendario?.Tipo || "";

// Usar valores predeterminados si eventosVerdes es null o sus propiedades no existen
const ubicacion = eventosVerdes?.location || "Dirección no especificada";
const idInput = eventosVerdes?.id || "";
const idEvento = eventosVerdes?.id || "";
const summary = eventosVerdes?.summary || "";

// 3. Obtener tiempo dinámico desde Google Sheets
// Convertir el valor de tiempo a minutos
let tiempoVisitaMinutos = 30; // Valor por defecto

if (datosPropiedad?.Tiempo) {
  const tiempoTexto = datosPropiedad.Tiempo.toString().toLowerCase();

  // Extraer número del texto (ej: "1hs" -> 1, "30mins" -> 30)
  const numeroMatch = tiempoTexto.match(/\d+/);

  if (numeroMatch) {
    const numero = parseInt(numeroMatch[0]);

    // Determinar si son horas o minutos
    if (tiempoTexto.includes('h') || tiempoTexto.includes('hora')) {
      tiempoVisitaMinutos = numero * 60; // Convertir horas a minutos
    } else if (tiempoTexto.includes('min')) {
      tiempoVisitaMinutos = numero;
    } else {
      // Si no especifica unidad, asumir que son minutos
      tiempoVisitaMinutos = numero;
    }
  }
}

console.log(`Tiempo de visita configurado: ${tiempoVisitaMinutos} minutos`);

// 4. Tomar la **fecha elegida por el usuario** con verificación
if (!datosCalendario?.fecha_cita) {
  return [{
    json: {
      error: "No se proporcionó una fecha de cita válida"
    }
  }];
}

const fechaCitaOriginal = datosCalendario.fecha_cita; // EJ: 2025-04-08T14:30:00-03:00

// 5. Parseo correcto SIN cambio de zona
const [fechaParte, horaParteCompleta] = fechaCitaOriginal.split('T');
const [horaParte] = horaParteCompleta.split('-'); // Agarra solo la hora limpia

// Crear la fecha base
const [anio, mes, dia] = fechaParte.split('-').map(Number);
const [hora, minutos, segundos] = horaParte.split(':').map(Number);
const fechaInicio = new Date(anio, mes - 1, dia, hora, minutos, segundos);

// 6. Sumar el tiempo dinámico de la visita
const fechaFin = new Date(fechaInicio.getTime() + tiempoVisitaMinutos * 60000);

// 7. Formatear fecha
const formatDate = (date) => {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0');
  const day = String(date.getDate()).padStart(2, '0');
  const hour = String(date.getHours()).padStart(2, '0');
  const minute = String(date.getMinutes()).padStart(2, '0');
  const second = String(date.getSeconds()).padStart(2, '0');

  return `${year}-${month}-${day}T${hour}:${minute}:${second}.000-03:00`;
};

const fechaInicioFormateada = formatDate(fechaInicio);
const fechaFinFormateada = formatDate(fechaFin);

// 8. Crear el objeto de salida
return [{
  json: {
    idInput,
    idEvento,
    colorId: "10", // Añadido explícitamente el colorId 10
    summary: `Dirección: ${ubicacion} - Interesado: ${nombreCliente}${nombreInmobiliaria ? ' - ' + nombreInmobiliaria : ''}`,
    description: ubicacion,
    tipoPropiedad,
    nombreCliente,
    telefono,
    nombreInmobiliaria,
    fechaInicio: fechaInicioFormateada,
    fechaFin: fechaFinFormateada,
    tiempoVisita: `${tiempoVisitaMinutos} minutos` // Para debugging
  }
}];

// Obtener TODOS los eventos rojos y la fecha que está intentando reservar
let eventosRojosData = $input.first().json.eventosRojos;
const fechaCitaUsuario = $('Calendario').first().json.fecha_cita;

console.log("=== VERIFICADOR DE DISPONIBILIDAD ===");
console.log("Fecha solicitada por el usuario:", fechaCitaUsuario);
console.log("Eventos rojos recibidos:", JSON.stringify(eventosRojosData, null, 2));

// Validar que tenemos datos
if (!eventosRojosData) {
    console.log("❌ No hay eventos rojos para verificar");
    return [{
        json: {
            disponible: true,
            mensaje: "✅ Horario disponible (no hay eventos para verificar).",
            debug: { razon: "eventosRojosData está vacío o undefined" }
        }
    }];
}

if (!fechaCitaUsuario) {
    console.log("❌ No se proporcionó fecha de cita");
    return [{
        json: {
            disponible: false,
            mensaje: "🚫 Error: No se proporcionó fecha de cita.",
            debug: { razon: "fechaCitaUsuario está vacío o undefined" }
        }
    }];
}

// Parsear la fecha de la cita del usuario (formato: 2025-05-30T12:00:00-03:00)
const fechaCitaDate = new Date(fechaCitaUsuario);
if (isNaN(fechaCitaDate.getTime())) {
    console.log("❌ Fecha de cita inválida:", fechaCitaUsuario);
    return [{
        json: {
            disponible: false,
            mensaje: "🚫 Error: Fecha de cita inválida.",
            debug: { razon: "No se pudo parsear fechaCitaUsuario", fechaOriginal: fechaCitaUsuario }
        }
    }];
}

console.log("Fecha de cita original:", fechaCitaUsuario);
console.log("Fecha de cita parseada a UTC:", fechaCitaDate.toISOString());
console.log("Fecha de cita en timestamp:", fechaCitaDate.getTime());

// Normalizar los eventos a un array
let todosLosEventos = [];
if (Array.isArray(eventosRojosData)) {
    todosLosEventos = eventosRojosData;
} else if (eventosRojosData && typeof eventosRojosData === 'object') {
    todosLosEventos = [eventosRojosData];
}

console.log(`\nTotal de eventos a analizar: ${todosLosEventos.length}`);

// Variables para el análisis
let conflictoEncontrado = false;
let eventosAnalizados = 0;
let eventosRojosEncontrados = 0;
let eventosConConflicto = [];
let todosLosDetalles = [];

// Analizar CADA evento
todosLosEventos.forEach((evento, index) => {
    eventosAnalizados++;

    console.log(`\n--- EVENTO ${index + 1} ---`);

    // Guardar detalles del evento para debug
    const detalleEvento = {
        index: index + 1,
        colorId: evento.colorId,
        tipo: evento.tipo,
        summary: evento.summary || "Sin título",
        start: evento.start,
        end: evento.end,
        esEventoRojo: false,
        tieneConflictoTiempo: false,
        razonDescarte: null
    };

    // Verificar si es un evento rojo (sin filtrar por color aún)
    const tipo = evento.tipo;
    const esRojo = tipo === "rojo" || tipo === "Rojo" || String(tipo).toLowerCase() === "rojo";

    console.log(`Tipo del evento: "${tipo}" -> ¿Es rojo? ${esRojo}`);

    if (!esRojo) {
        detalleEvento.razonDescarte = `Tipo "${tipo}" no es "rojo"`;
        console.log(`❌ Descartado: ${detalleEvento.razonDescarte}`);
        todosLosDetalles.push(detalleEvento);
        return; // Siguiente evento
    }

    // Es un evento rojo
    eventosRojosEncontrados++;
    detalleEvento.esEventoRojo = true;

    console.log("✅ Es un evento ROJO, verificando horarios...");
    console.log("Detalles del evento:", JSON.stringify(evento, null, 2));

    // Obtener fechas del evento
    let fechaEventoInicio, fechaEventoFin;

    // Intentar múltiples formatos de fecha
    if (evento.start && evento.start.dateTime) {
        fechaEventoInicio = new Date(evento.start.dateTime);
        console.log("Fecha inicio desde start.dateTime:", evento.start.dateTime);
    } else if (evento.start && typeof evento.start === 'string') {
        fechaEventoInicio = new Date(evento.start);
        console.log("Fecha inicio desde start (string):", evento.start);
    } else if (evento.start && evento.start.date) {
        fechaEventoInicio = new Date(evento.start.date);
        console.log("Fecha inicio desde start.date:", evento.start.date);
    }

    if (evento.end && evento.end.dateTime) {
        fechaEventoFin = new Date(evento.end.dateTime);
        console.log("Fecha fin desde end.dateTime:", evento.end.dateTime);
    } else if (evento.end && typeof evento.end === 'string') {
        fechaEventoFin = new Date(evento.end);
        console.log("Fecha fin desde end (string):", evento.end);
    } else if (evento.end && evento.end.date) {
        fechaEventoFin = new Date(evento.end.date);
        console.log("Fecha fin desde end.date:", evento.end.date);
    }

    // Validar que se pudieron obtener las fechas
    if (!fechaEventoInicio || isNaN(fechaEventoInicio.getTime())) {
        detalleEvento.razonDescarte = "No se pudo parsear fecha de inicio";
        console.log(`❌ ${detalleEvento.razonDescarte}`);
        todosLosDetalles.push(detalleEvento);
        return;
    }

    if (!fechaEventoFin || isNaN(fechaEventoFin.getTime())) {
        detalleEvento.razonDescarte = "No se pudo parsear fecha de fin";
        console.log(`❌ ${detalleEvento.razonDescarte}`);
        todosLosDetalles.push(detalleEvento);
        return;
    }

    // Guardar fechas parseadas en el detalle
    detalleEvento.fechaInicioParsed = fechaEventoInicio.toISOString();
    detalleEvento.fechaFinParsed = fechaEventoFin.toISOString();

    console.log(`Evento inicia: ${fechaEventoInicio.toISOString()} (timestamp: ${fechaEventoInicio.getTime()})`);
    console.log(`Evento termina: ${fechaEventoFin.toISOString()} (timestamp: ${fechaEventoFin.getTime()})`);
    console.log(`Cita solicitada: ${fechaCitaDate.toISOString()} (timestamp: ${fechaCitaDate.getTime()})`);

    // Verificar superposición de tiempo con comparación de timestamps para mayor precisión
    // La cita conflicta si:
    // 1. La cita está dentro del rango del evento existente
    // 2. La cita empieza exactamente cuando empieza el evento

    const timestampCita = fechaCitaDate.getTime();
    const timestampEventoInicio = fechaEventoInicio.getTime();
    const timestampEventoFin = fechaEventoFin.getTime();

    const citaDentroDelEvento = timestampCita >= timestampEventoInicio && timestampCita < timestampEventoFin;
    const citaEsMismaHoraInicio = timestampCita === timestampEventoInicio;

    console.log(`¿Cita dentro del evento? ${citaDentroDelEvento} (${timestampCita} >= ${timestampEventoInicio} && ${timestampCita} < ${timestampEventoFin})`);
    console.log(`¿Cita es misma hora de inicio? ${citaEsMismaHoraInicio} (${timestampCita} === ${timestampEventoInicio})`);

    const hayConflicto = citaDentroDelEvento || citaEsMismaHoraInicio;
    detalleEvento.tieneConflictoTiempo = hayConflicto;

    if (hayConflicto) {
        console.log("🚨 ¡CONFLICTO DETECTADO!");
        conflictoEncontrado = true;
        eventosConConflicto.push({
            evento: index + 1,
            summary: evento.summary || "Sin título",
            tipo: evento.tipo,
            colorId: evento.colorId,
            inicioEvento: fechaEventoInicio.toISOString(),
            finEvento: fechaEventoFin.toISOString(),
            citaSolicitada: fechaCitaDate.toISOString()
        });
    } else {
        console.log("✅ No hay conflicto de tiempo");
    }

    todosLosDetalles.push(detalleEvento);
});

// Resultado final
const disponible = !conflictoEncontrado;

console.log("\n=== RESUMEN FINAL ===");
console.log(`Eventos analizados: ${eventosAnalizados}`);
console.log(`Eventos rojos encontrados: ${eventosRojosEncontrados}`);
console.log(`Conflictos detectados: ${eventosConConflicto.length}`);
console.log(`Resultado: ${disponible ? "DISPONIBLE" : "NO DISPONIBLE"}`);

if (eventosConConflicto.length > 0) {
    console.log("Detalles de conflictos:");
    eventosConConflicto.forEach(conflicto => {
        console.log(`- Evento "${conflicto.summary}" del ${conflicto.inicioEvento} al ${conflicto.finEvento}`);
    });
}

return [{
    json: {
        disponible: disponible,
        mensaje: disponible 
            ? "✅ Horario disponible, se puede agendar."
            : `🚫 El horario ya está ocupado por ${eventosConConflicto.length} evento(s). Elegí otra fecha.`,
        debug: {
            fechaCitaSolicitada: fechaCitaDate.toISOString(),
            totalEventosAnalizados: eventosAnalizados,
            eventosRojosEncontrados: eventosRojosEncontrados,
            conflictosDetectados: eventosConConflicto.length,
            detallesConflictos: eventosConConflicto,
            todosLosEventosAnalizados: todosLosDetalles
        }
    }
}];

