// Obtener la fecha ISO y la dirección
const fechaISO = $('Calendario').first().json.fecha_cita;
const direccionCompleta = $('Propietario').first().json.location;

// Obtener datos adicionales
const nombreInteresado = $('Calendario').first().json.Nombre || "No disponible";
const telefonoCliente = $('Calendario').first().json.numero_cliente || "No disponible";
const nombreInmobiliaria = $('Calendario').first().json.nombre_inmobiliaria || "No especificada";

// Función para extraer todos los componentes de la fecha
function extraerComponentesFecha(fechaISO) {
  // Si no hay fecha, devolver valores predeterminados
  if (!fechaISO) return {
    dia: "No disponible",
    fecha: "No disponible",
    hora: "No disponible"
  };

  try {
    // Crear un objeto Date a partir de la fecha ISO
    const fecha = new Date(fechaISO);

    // Extraer día de la semana
    const diasSemana = ["Domingo", "Lunes", "Martes", "Miércoles", "Jueves", "Viernes", "Sábado"];
    const diaSemana = diasSemana[fecha.getDay()];

    // Extraer componentes de fecha
    const dia = fecha.getDate().toString().padStart(2, '0');
    const mes = (fecha.getMonth() + 1).toString().padStart(2, '0');
    const anio = fecha.getFullYear();

    // Extraer componentes de hora
    const horas = fecha.getHours();
    const minutos = fecha.getMinutes().toString().padStart(2, '0');

    // Formatear cada componente
    return {
      dia: diaSemana,
      fecha: `${dia}-${mes}-${anio}`,
      hora: `${horas}:${minutos}hs`
    };
  } catch (error) {
    console.error("Error al extraer componentes de la fecha:", error);
    return {
      dia: "Error",
      fecha: "Error",
      hora: "Error"
    };
  }
}

// Extraer componentes de la fecha
const componentes = extraerComponentesFecha(fechaISO);

// Procesar dirección
function procesarDireccion(direccionCompleta) {
  if (!direccionCompleta) return "Dirección no disponible";

  // Aquí puedes agregar lógica adicional para formatear la dirección si es necesario
  return direccionCompleta;
}

// Obtener la dirección formateada
const direccion = procesarDireccion(direccionCompleta);

// Formatear teléfono (si es necesario)
function formatearTelefono(telefono) {
  if (!telefono) return "No disponible";

  // Aquí puedes agregar lógica para formatear el teléfono si es necesario
  return telefono;
}

// Teléfono formateado
const telefono = formatearTelefono(telefonoCliente);

// Mostrar los resultados
console.log("Día de la semana:", componentes.dia);
console.log("Fecha:", componentes.fecha);
console.log("Hora:", componentes.hora);
console.log("Dirección:", direccion);
console.log("Interesado:", nombreInteresado);
console.log("Teléfono:", telefono);
console.log("Inmobiliaria:", nombreInmobiliaria);

// Para usar directamente en tu flujo
return [{
  json: {
    dia_semana: componentes.dia,
    fecha: componentes.fecha,
    hora: componentes.hora,
    direccion: direccion,
    interesado: nombreInteresado,
    telefono: telefono,
    inmobiliaria: nombreInmobiliaria
  }
}];

// Este código toma una fecha de cita y calcula cuándo deben enviarse los recordatorios
// Obtener la fecha de cita del input
const fechaCita = $('Calendario').first().json.fecha_cita;
console.log(`Fecha de cita original: ${fechaCita}`);

// Crear objetos Date para poder manipular las fechas
const citaDate = new Date(fechaCita);

// Calcular momento para recordatorio de 24 horas (1 día antes, misma hora)
const recordatorio24hDate = new Date(citaDate);
recordatorio24hDate.setDate(recordatorio24hDate.getDate() - 1);

// Calcular momento para recordatorio de 1 hora (misma fecha, 1 hora antes)
const recordatorio1hDate = new Date(citaDate);
recordatorio1hDate.setHours(recordatorio1hDate.getHours() - 1);

// Función para formatear fechas en formato YYYY-MM-DDTHH:MM
function formatearFechaSimplificada(fecha) {
  // Obtener componentes de la fecha
  const year = fecha.getFullYear();
  const month = String(fecha.getMonth() + 1).padStart(2, '0');
  const day = String(fecha.getDate()).padStart(2, '0');
  const hours = String(fecha.getHours()).padStart(2, '0');
  const minutes = String(fecha.getMinutes()).padStart(2, '0');

  // Construir el string con formato YYYY-MM-DDTHH:MM
  return `${year}-${month}-${day}T${hours}:${minutes}`;
}

// Formatear las fechas al formato simplificado
const fechaCitaFormateada = formatearFechaSimplificada(citaDate);
const recordatorio24h = formatearFechaSimplificada(recordatorio24hDate);
const recordatorio1h = formatearFechaSimplificada(recordatorio1hDate);

// Log para verificar los cálculos
console.log(`Fecha de cita: ${fechaCitaFormateada}`);
console.log(`Momento para recordatorio 24h: ${recordatorio24h}`);
console.log(`Momento para recordatorio 1h: ${recordatorio1h}`);

// Retornar los resultados
return {
  json: {
    fecha_cita: fechaCitaFormateada,
    recordatorio_24h: recordatorio24h,
    recordatorio_1h: recordatorio1h
  }
};
