// --- Funciones de procesamiento de texto ---

// Función para procesar el texto y dividirlo inteligentemente
function processAndSplitText(textInput) {
  // Asegúrate de que textInput sea un string o pueda ser convertido
  let text = textInput;

  // Si la entrada no es un string
  if (typeof text !== 'string') {
    // Si es null o undefined, simplemente devolvemos un array vacío
    if (text === null || text === undefined) {
      return [];
    }

    // Si es un objeto, intentamos encontrar campos comunes que contengan texto
    if (typeof text === 'object') {
      if (text.text) {
        text = text.text;
      } else if (text.message) {
        text = text.message;
      } else if (text.type === 'text' && text.text) {
        text = text.text;
      } else if (text.output) { // Intentamos extraer de un campo 'output'
          // Si output es un string, lo usamos
          if (typeof text.output === 'string') {
              text = text.output;
          } else { // Si output es un objeto o array, intentamos extraer de ahí
              const extracted = extractTextContent(text.output); // Usamos la función de extracción
              if (extracted) {
                  text = extracted;
              } else {
                   // Si no pudimos extraer, intentamos convertir todo el objeto a string
                   try {
                       text = JSON.stringify(text);
                   } catch (e) {
                       console.error("No se pudo serializar el objeto a string:", e);
                       return []; // Si falla la serialización, devolvemos vacío
                   }
              }
          }
      } else {
        // Si no encontramos campos de texto comunes ni 'output', intentamos convertir a string
        try {
          text = JSON.stringify(text);
        } catch (e) {
           console.error("No se pudo serializar el objeto a string:", e);
           return []; // Si falla la serialización, devolvemos vacío
        }
      }
    } else { // Si no es string, objeto, null o undefined, devolvemos vacío
       console.warn("Entrada a processAndSplitText no es string, objeto, null o undefined:", typeof text);
       return [];
    }
  }

  // Si después de los intentos no tenemos un string válido, devolvemos vacío
  if (typeof text !== 'string' || text.trim() === '') {
      return [];
  }

  // Ahora que tenemos un string, procesamos el formato
  const processedText = text
    .replace(/\*\*(.+?)\*\*/g, '*$1*') // **texto** -> *texto*
    .replace(/###\s*/g, '')         // Elimina encabezados ###
    .replace(/[¡¿!]/g, '');         // Elimina signos de exclamación e interrogación iniciales y finales

  // Divide en líneas para análisis
  const lines = processedText.split('\n');

  // Grupos para almacenar los mensajes
  const messages = [];
  let currentMessage = [];
  let inNumberedSection = false;
  let currentSectionNumber = null;

  // Detectar secciones numeradas y agrupables
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    // Detecta si la línea es un encabezado numerado (ej: "1. Tipo de propiedad:")
    const numberedHeaderMatch = line.match(/^\s*(\d+)\.\s+([^:]+):/);

    if (numberedHeaderMatch) {
      const sectionNumber = parseInt(numberedHeaderMatch[1]);

      // Si estamos empezando una nueva sección numerada O si el número no es el siguiente esperado
      if (!inNumberedSection || (inNumberedSection && sectionNumber !== currentSectionNumber + 1)) {
         // Si tenemos contenido previo, guardamos como mensaje separado
         if (currentMessage.length > 0) {
             messages.push(currentMessage.join('\n').trim()); // Trim antes de guardar
             currentMessage = [];
         }
         inNumberedSection = true;
      }
      currentSectionNumber = sectionNumber; // Actualizamos el número de sección actual
       currentMessage.push(line); // Agregamos la línea al mensaje actual

    } else if (line.trim() === '') { // Línea vacía
        // Una línea vacía puede terminar una sección si hay contenido previo
        if (currentMessage.length > 0) {
             // Si no estamos en una sección numerada, una línea vacía termina el mensaje actual
             if (!inNumberedSection) {
                 messages.push(currentMessage.join('\n').trim()); // Trim antes de guardar
                 currentMessage = [];
             } else {
                 // Si estamos en una sección numerada, una línea vacía se agrega al mensaje actual,
                 // podría terminar la sección si hay otra línea vacía o fin de texto después.
                 currentMessage.push(line);
             }
        }
         // Si currentMessage está vacío, una línea vacía consecutiva no hace nada

    } else { // Línea con contenido que no es un encabezado numerado
        currentMessage.push(line);
        inNumberedSection = false; // Salimos de la sección numerada si el contenido no sigue el patrón
    }
  }

  // Agregar el último mensaje si queda algo
  if (currentMessage.length > 0) {
    messages.push(currentMessage.join('\n').trim()); // Trim antes de guardar
  }

  // Filtrar mensajes vacíos y limpiar líneas vacías extra
  return messages
    .filter(msg => msg.length > 0) // Filtrar cadenas vacías después del trim
    .map(msg => {
      // Eliminar líneas vacías múltiples dentro del mensaje
      return msg.replace(/\n{2,}/g, '\n\n');
    });
}

// Función para extraer texto de diferentes estructuras de datos
function extractTextContent(data) {
  // Si la data es null o undefined, retornamos null inmediatamente
  if (data === null || data === undefined) {
    return null;
  }

  // Si es un string, lo retornamos directamente
  if (typeof data === 'string') {
    return data;
  }

  // Si es un array
  if (Array.isArray(data)) {
    // Iteramos sobre el array e intentamos extraer texto de cada elemento
    for (const item of data) {
       const extracted = extractTextContent(item); // Llamada recursiva para elementos del array
       if (extracted) {
           return extracted; // Devolvemos el primer texto que encontramos
       }
    }
    return null; // Si no encontramos texto en ningún elemento del array
  }

  // Si es un objeto
  if (typeof data === 'object') {
    // Priorizamos campos comunes que suelen contener texto
    if (data.text) {
      return data.text;
    }
    if (data.message) {
      return data.message;
    }
    // Si tiene un campo 'output', intentamos extraer texto de él (puede ser string, array u objeto)
    if (data.output !== undefined && data.output !== null) {
         const extracted = extractTextContent(data.output); // Llamada recursiva para el campo output
         if (extracted) {
             return extracted;
         }
    }
     // Si tiene un campo 'response', intentamos extraer texto de él
     if (data.response !== undefined && data.response !== null) {
         const extracted = extractTextContent(data.response); // Llamada recursiva para el campo response
         if (extracted) {
             return extracted;
         }
    }
     // Si tiene un campo 'json', intentamos extraer texto de él
     if (data.json !== undefined && data.json !== null) {
         const extracted = extractTextContent(data.json); // Llamada recursiva para el campo json
         if (extracted) {
             return extracted;
         }
    }
    // Si no encontramos campos de texto comunes ni estructuras anidadas esperadas,
    // intentamos convertir el objeto completo a string como último recurso
     try {
         return JSON.stringify(data);
     } catch (e) {
         console.error("No se pudo serializar el objeto a string en extractTextContent:", e);
         return null; // Si falla la serialización
     }
  }

  // Si el tipo de dato no es manejado, retornamos null
  console.warn("Tipo de dato no manejado en extractTextContent:", typeof data);
  return null;
}

// --- Lógica Principal con manejo seguro de nodos ---
let textToProcess = null;

try {
  // Verificar si el nodo "Cancelar evento" existe y tiene datos
  let cancelarEventoData = null;
  try {
    // Usamos try-catch para capturar errores si el nodo no existe
    cancelarEventoData = $('Cancelar evento');
    if (cancelarEventoData && cancelarEventoData.first() && cancelarEventoData.first().json) {
      if (cancelarEventoData.first().json.response !== undefined) {
        textToProcess = extractTextContent(cancelarEventoData.first().json.response);
      } else {
        textToProcess = extractTextContent(cancelarEventoData.first().json);
      }
    }
  } catch (e) {
    // Silenciosamente ignoramos errores si el nodo no existe
    console.log("Nodo 'Cancelar evento' no disponible o sin datos válidos");
  }

  // Solo intentamos acceder a AGE3 si aún no hemos encontrado texto para procesar
  if (!textToProcess) {
    try {
      // Usamos try-catch para capturar errores si el nodo no existe
      const age3Data = $('AGE3');
      if (age3Data && age3Data.first() && age3Data.first().json) {
        if (age3Data.first().json.output !== undefined) {
          textToProcess = extractTextContent(age3Data.first().json.output);
        } else {
          textToProcess = extractTextContent(age3Data.first().json);
        }
      }
    } catch (e) {
      // Silenciosamente ignoramos errores si el nodo no existe
      console.log("Nodo 'AGE3' no disponible o sin datos válidos");
    }
  }

  // Si no hemos encontrado datos en los nodos específicos, intentamos con $input directamente
  if (!textToProcess) {
    try {
      const inputItems = $input.all();
      if (inputItems && inputItems.length > 0 && inputItems[0].json) {
        textToProcess = extractTextContent(inputItems[0].json);
      }
    } catch (e) {
      console.log("No se pudo acceder a los datos de entrada directamente:", e.message);
    }
  }

  // Procesar y retornar el resultado
  if (textToProcess) {
    const textArray = processAndSplitText(textToProcess);
    // Devolvemos un array con un objeto que contiene el array de texto
    return [{json: {text: textArray}}];
  } else {
    // Si no se pudo obtener ni procesar texto, devolvemos un array vacío
    return [{json: {text: []}}];
  }
} catch (error) {
  // Capturamos cualquier error no manejado y devolvemos un objeto con información del error
  console.error("Error general en el nodo:", error.message);
  return [{json: {text: [], error: error.message}}];
}

// Función principal para procesar la selección o mensaje
const procesarSeleccion = () => {
  try {
    // Obtener el ListResponseMessage desde el input
    const listResponseMessage = $input.first()?.json?.ListResponseMessage;

    // Variables solicitadas para incluir en cualquier respuesta
    const listTitle = listResponseMessage?.title || "";
    const singleSelectReply = listResponseMessage?.singleSelectReply || null;

    // Obtener el eventId directamente de la ruta especificada
    if (listResponseMessage && listResponseMessage.singleSelectReply) {
      const eventId = listResponseMessage.singleSelectReply.selectedRowId;

      if (eventId) {
        console.log("eventId encontrado:", eventId);

        // Obtener el título y la descripción
        const title = listResponseMessage.title || "";

        // Verificar si hay información adicional en el nodo que indique el tipo de lista
        // Por ejemplo, podríamos buscar en quotedMessage o contextInfo
        let tipoLista = "";

        // Intentar obtener tipo de lista desde lista completa si está disponible
        try {
          const quotedMessage = listResponseMessage?.contextInfo?.quotedMessage;
          if (quotedMessage && quotedMessage.listMessage) {
            const listMessageTitle = quotedMessage.listMessage.title || "";
            if (listMessageTitle.includes("Visitas Programadas")) {
              tipoLista = "fechas_reprogramacion";
            } else if (listMessageTitle.includes("Fechas Disponibles")) {
              tipoLista = "fechas_disponibles";
            }
          }
        } catch (e) {
          console.log("Error al intentar obtener tipo de lista desde quoted message:", e);
        }

        // Si no pudimos detectar el tipo desde el contexto, intentamos inferirlo
        if (!tipoLista) {
          // Buscar elementos específicos en el input que puedan indicar el tipo
          const inputJson = $input.first()?.json || {};

          // SOLUCIÓN: Verificar si hay algún indicador en el formato del rowId o en los datos
          if (eventId.match(/^\d+-\d+-\d+$/)) {
            // Si tiene formato de fecha (mes-dia-hora), asumimos que es fechas_disponibles
            tipoLista = "fechas_disponibles";
          } else if (title.includes("Visitas Programadas") || 
                   inputJson.footerText?.includes("visitas") ||
                   inputJson.description?.includes("visitas programadas")) {
            tipoLista = "fechas_reprogramacion";
          }

          // Si aún no tenemos tipo, hacemos una última verificación
          if (!tipoLista) {
            if (title.match(/\d+ de [A-Za-z]+ - \d+:\d+/)) {
              tipoLista = "fechas_disponibles"; // Si tiene formato de fecha, es disponibilidad
            } else {
              tipoLista = "disponibilidad"; // Valor por defecto
            }
          }
        }

        // Usamos el tipo detectado
        if (tipoLista === "fechas_reprogramacion") {
          // NUEVO: Extraer fecha del título para casos de fechas_reprogramacion
          const fechaMatch = title.match(/(\d+) de ([A-Za-zÁáÉéÍíÓóÚú]+) - (\d+):(\d+)/i);
          let detallesVisita = {
            visita_id: eventId,
            texto_visita: title
          };

          // Si encontramos un formato de fecha en el título, agregamos fecha_iso
          if (fechaMatch) {
            const dia = fechaMatch[1];
            const mesTexto = fechaMatch[2];
            const hora = fechaMatch[3];
            const minutos = fechaMatch[4];

            // Mapeo de nombres de meses a números
            const mesesMap = {
              'enero': '1', 'febrero': '2', 'marzo': '3', 'abril': '4',
              'mayo': '5', 'junio': '6', 'julio': '7', 'agosto': '8',
              'septiembre': '9', 'octubre': '10', 'noviembre': '11', 'diciembre': '12'
            };

            const mesNumero = mesesMap[mesTexto.toLowerCase()];
            if (mesNumero) {
              const now = new Date();
              const anioActual = now.getFullYear();
              // Sin segundos
              const fechaISO = `${anioActual}-${mesNumero.padStart(2, '0')}-${dia.padStart(2, '0')}T${hora.padStart(2, '0')}:${minutos.padStart(2, '0')}`;

              detallesVisita = {
                ...detallesVisita,
                dia: dia,
                mes: mesNumero,
                mes_nombre: mesTexto,
                hora: `${hora}:${minutos}`,
                fecha_iso: fechaISO
              };
            }
          }

          return {
            tipo: "fechas_reprogramacion",
            eventId: eventId,
            detalles: detallesVisita,
            listTitle: listTitle,
            singleSelectReply: singleSelectReply
          };
        } else {
          // Para fechas_disponibles o disponibilidad
          // Procesamos la fecha
          if (eventId.match(/^\d+-\d+-\d+$/)) {
            const partes = eventId.split('-');
            const mesNumero = partes[0];
            const dia = partes[1];

            // Formatea la hora
            let horarioRaw = partes[2].toString().padStart(4, '0');
            const hora = horarioRaw.slice(0, 2);
            const minutos = horarioRaw.slice(2, 4);
            const horarioFormateado = `${hora}:${minutos}`;

            // Fecha ISO - Sin segundos
            const now = new Date();
            const anioActual = now.getFullYear();
            const fechaISO = `${anioActual}-${mesNumero.padStart(2, '0')}-${dia.padStart(2, '0')}T${horarioFormateado}`;

            const nombresMeses = {
              '1': 'Enero', '2': 'Febrero', '3': 'Marzo', '4': 'Abril',
              '5': 'Mayo', '6': 'Junio', '7': 'Julio', '8': 'Agosto',
              '9': 'Septiembre', '10': 'Octubre', '11': 'Noviembre', '12': 'Diciembre'
            };

            return {
              tipo: tipoLista, // "fechas_disponibles" o el tipo detectado
              detalles: {
                dia: dia,
                mes: mesNumero,
                mes_nombre: nombresMeses[mesNumero],
                hora: horarioFormateado,
                fecha_iso: fechaISO,
                texto_legible: `${dia} de ${nombresMeses[mesNumero]} a las ${horarioFormateado}`
              },
              listTitle: listTitle,
              singleSelectReply: singleSelectReply
            };
          } else {
            // Extraer información del title
            const fechaMatch = title.match(/(\d+) de ([A-Za-zÁáÉéÍíÓóÚú]+) - (\d+):(\d+)/i);

            if (fechaMatch) {
              const dia = fechaMatch[1];
              const mesTexto = fechaMatch[2];
              const hora = fechaMatch[3];
              const minutos = fechaMatch[4];

              // Mapeo de nombres de meses a números
              const mesesMap = {
                'enero': '1', 'febrero': '2', 'marzo': '3', 'abril': '4',
                'mayo': '5', 'junio': '6', 'julio': '7', 'agosto': '8',
                'septiembre': '9', 'octubre': '10', 'noviembre': '11', 'diciembre': '12'
              };

              const mesNumero = mesesMap[mesTexto.toLowerCase()];
              if (mesNumero) {
                const now = new Date();
                const anioActual = now.getFullYear();
                // Sin segundos
                const fechaISO = `${anioActual}-${mesNumero.padStart(2, '0')}-${dia.padStart(2, '0')}T${hora.padStart(2, '0')}:${minutos.padStart(2, '0')}`;

                return {
                  tipo: tipoLista, // "fechas_disponibles" o el tipo detectado
                  detalles: {
                    dia: dia,
                    mes: mesNumero,
                    mes_nombre: mesTexto,
                    hora: `${hora}:${minutos}`,
                    fecha_iso: fechaISO,
                    texto_legible: title
                  },
                  listTitle: listTitle,
                  singleSelectReply: singleSelectReply
                };
              }
            }

            // Fallback para selecciones desconocidas
            return {
              tipo: "seleccion_simple",
              eventId: eventId,
              title: title || "Selección",
              listTitle: listTitle,
              singleSelectReply: singleSelectReply
            };
          }
        }
      }
    }

    // Resto del código para mensajes y confirmaciones igual...
    // Verificar si es un mensaje de texto (confirmación)
    const mensaje = $input.first()?.json?.mensaje || "";
    if (typeof mensaje === 'string' && mensaje.toLowerCase) {
      const mensajeTexto = mensaje.toLowerCase();
      if (mensajeTexto.includes("son correctos") || mensajeTexto === "si" || 
          mensajeTexto === "sí" || mensajeTexto.includes("si claro")) {
        return {
          tipo: "confirmacion_datos",
          mensaje: mensajeTexto,
          listTitle: listTitle,
          singleSelectReply: singleSelectReply
        };
      }

      if (mensajeTexto.length > 0) {
        return {
          tipo: "mensaje_normal",
          mensaje: mensaje,
          listTitle: listTitle,
          singleSelectReply: singleSelectReply
        };
      }
    }

    return {
      tipo: "mensaje_normal",
      mensaje: mensaje || "",
      listTitle: listTitle,
      singleSelectReply: singleSelectReply
    };

  } catch (error) {
    console.error("Error procesando selección:", error);
    return {
      error: true,
      mensaje: error.message,
      listTitle: "",
      singleSelectReply: null
    };
  }
};

