const data = $item(0).$node["Compara Get Memory"].json["Redis2"];

// Verifica si data es un array directamente
let array;

if (Array.isArray(data)) {
    array = data;
} else {
    try {
        // Intenta parsear si es una cadena JSON v√°lida
        array = JSON.parse(data);
    } catch (error) {
        // Si no es JSON, considera que es un texto plano
        array = [data];
    }
}

// Une los elementos del array en un string
const message_completo = array.join(" ");

return [{ json: { message_completo } }];
