const wordCount = words.length;

// Contar caracteres en el mensaje
const charCount = userMessage.length;

// Detectar si el mensaje contiene solo palabras de 6 caracteres o menos
const isShortMessage = words.every(word => word.length <= 6);

// Detectar si el mensaje es numérico (puede ser un solo número o varios números separados por espacio)
const isNumericMessage = /^[0-9\s]+$/.test(userMessage);

// Detectar si es un mensaje corto de respuesta rápida
const quickReplies = ["ok", "sí", "dale", "hola", "gracias", "entendido", "bien", "cómo estás", "adiós"];
const isQuickReply = quickReplies.includes(userMessage);

// 🚀 **Lógica del delay**
