// Obtener el texto de entrada
const inputText = $input.first().json.output;

// Crear un array para almacenar los diferentes mensajes
const mensajes = [];

// Separar la introducciÃ³n, datos y conclusiÃ³n
const lines = inputText.split('\n');

// Identificar si una lÃ­nea contiene datos con asteriscos
const containsAsterisks = line => line.includes('**');

// Encontrar los Ã­ndices donde comienzan y terminan los datos
const firstDataIndex = lines.findIndex(containsAsterisks);
const lastDataIndex = [...lines].reverse().findIndex(containsAsterisks);
const lastDataPos = lastDataIndex >= 0 ? lines.length - 1 - lastDataIndex : -1;

// Extraer las partes del mensaje
let intro = '';
let datos = '';
let conclusion = '';

// Obtener la introducciÃ³n (texto antes de los datos)
if (firstDataIndex > 0) {
  intro = lines.slice(0, firstDataIndex).filter(line => line.trim()).join('\n');
}

// Formatear los datos (lÃ­neas con asteriscos)
if (firstDataIndex >= 0) {
  // Para WhatsApp, necesitamos formatear cada lÃ­nea de manera especial
  // Extraemos las secciones numeradas de los datos
  const dataLines = [];

  // Procesar las lÃ­neas de datos para WhatsApp
  lines.slice(firstDataIndex, lastDataPos + 1)
    .filter(containsAsterisks)
    .forEach(line => {
      // Extraer el contenido entre asteriscos
      let cleanLine = line.replace(/\*\*(.+?)\*\*/g, '$1').trim();

      // Si la lÃ­nea ya tiene un nÃºmero al inicio como "1. ", lo mantenemos
      if (!cleanLine.match(/^\d+\.\s/)) {
        // Si no tiene nÃºmero, aÃ±adimos un bullet point
        cleanLine = "â€¢ " + cleanLine;
      }

      dataLines.push(cleanLine);
    });

  // Juntar las lÃ­neas formateadas para WhatsApp
  datos = dataLines.join('\n');
}

// Obtener la conclusiÃ³n (texto despuÃ©s de los datos)
if (lastDataPos >= 0 && lastDataPos < lines.length - 1) {
  conclusion = lines.slice(lastDataPos + 1).filter(line => line.trim()).join('\n');
}

// Procesar y aÃ±adir las partes al array de mensajes
if (intro) mensajes.push({ text: intro });
if (datos) mensajes.push({ text: datos });
if (conclusion) mensajes.push({ text: conclusion });

// Si no hay mensajes vÃ¡lidos, devolver el texto original
