// Obter os arrays de mensagens dos nós "Get Memory 1" e "Get Memory 2"
const Messages1 = $('Get Memory 2').item.json.Messages;
const Messages2 = $('Get Memory 1').item.json.Messages;

// Combinar as strings dentro de cada array em uma única string
const combinedText1 = Messages1.join(' ');
const combinedText2 = Messages2.join(' ');

// Retornar os dois textos combinados como variáveis separadas
return {
    combinedText1,
    combinedText2
};

// Obtém a saída do nó "AI Agent"
const output = $('AI Agent').item.json.output;

// Divide o texto em partes, usando dois espaços entre linhas como delimitador
const splitOutput = output.split('\n\n');

// Retorna o resultado como array de objetos
return splitOutput.map(part => ({ result: part }));
