// Teléfono dinámico del cliente
const remoteJid = $('Workflow Input Trigger').first().json.Telefono || "";

// Función para asignar emojis basados en el tipo de comida
function asignarEmojiComida(nombre) {
  const nombreLower = nombre.toLowerCase();

  // Empanadas y tartas
  if (nombreLower.includes('empanada')) return '🥟';
  if (nombreLower.includes('tarta')) return '🥧';

  // Carnes
  if (nombreLower.includes('asado') || nombreLower.includes('parrilla')) return '🥩';
  if (nombreLower.includes('milanesa')) return '🍖';
  if (nombreLower.includes('bife') || nombreLower.includes('carne')) return '🥩';
  if (nombreLower.includes('pollo')) return '🍗';
  if (nombreLower.includes('chorizo') || nombreLower.includes('morcilla')) return '🌭';

  // Pastas
  if (nombreLower.includes('pasta') || nombreLower.includes('fideos') || nombreLower.includes('ñoquis') || nombreLower.includes('ravioles')) return '🍝';
  if (nombreLower.includes('lasagna') || nombreLower.includes('lasaña')) return '🍝';

  // Pizza y similares
  if (nombreLower.includes('pizza')) return '🍕';
  if (nombreLower.includes('focaccia')) return '🍞';

  // Pescados y mariscos
  if (nombreLower.includes('pescado') || nombreLower.includes('merluza') || nombreLower.includes('salmón')) return '🐟';
  if (nombreLower.includes('camarón') || nombreLower.includes('langostino')) return '🦐';

  // Verduras y ensaladas
  if (nombreLower.includes('ensalada')) return '🥗';
  if (nombreLower.includes('verdura')) return '🥬';

  // Sopas
  if (nombreLower.includes('sopa') || nombreLower.includes('caldo')) return '🍲';

  // Postres
  if (nombreLower.includes('flan') || nombreLower.includes('postre')) return '🍮';
  if (nombreLower.includes('helado')) return '🍨';
  if (nombreLower.includes('torta')) return '🎂';

  // Bebidas
  if (nombreLower.includes('jugo') || nombreLower.includes('agua')) return '🥤';
  if (nombreLower.includes('café')) return '☕';
  if (nombreLower.includes('gaseosa') || nombreLower.includes('coca')) return '🥤';

  // Sandwich y hamburguesas
  if (nombreLower.includes('sandwich') || nombreLower.includes('sándwich')) return '🥪';
  if (nombreLower.includes('hamburguesa')) return '🍔';

  // Arroz y guisos
  if (nombreLower.includes('arroz')) return '🍚';
  if (nombreLower.includes('guiso') || nombreLower.includes('estofado')) return '🍲';

  // Default para comida general
  return '🍽️';
}

// Función para limpiar precio y formatear
function formatearPrecio(precio) {
  // Convertir a string y limpiar
  let precioLimpio = precio.toString().replace(/[^\d]/g, '');

  // Si es solo número, añadir $
  if (precioLimpio && !isNaN(precioLimpio)) {
    return `$${precioLimpio}`;
  }

  // Si ya tiene formato, devolverlo
  return precio.toString();
}

// Obtener datos de todos los elementos del nodo Limit1
const limitData = $input.all();

// Crear arrays para almacenar los datos
const productos = [];
const descripciones = [];
const precios = [];

// Procesar cada item del limit
limitData.forEach(item => {
  if (item.json && item.json.Producto) { // Solo procesar items que tengan producto
    productos.push(item.json.Producto);
    descripciones.push(item.json.Descripcion || "");
    precios.push(item.json.Precio || "0");
  }
});

// Validar que tengamos datos
const cantidad = productos.length;
const rows = [];

for (let i = 0; i < cantidad; i++) {
  const idProducto = `menu_${i + 1}`;
  const nombre = productos[i];
  const descripcion = descripciones[i] || "";
  const precio = precios[i] || "0";

  // Asignar emoji basado en el tipo de comida
  const emojiComida = asignarEmojiComida(nombre);
  const precioFormateado = formatearPrecio(precio);

  // INCLUIR PRECIO EN EL TÍTULO para que sea visible
  const tituloCompleto = `${emojiComida} ${nombre} - ${precioFormateado}`;

  // La descripción será solo la descripción del producto
  rows.push({
    id: idProducto,
    title: tituloCompleto,
    description: descripcion
  });
}

const sections = [
  {
    title: "🥶 MENÚ ANTIFRÍO x MENOS DE $5000 🥶",
    rows: rows
  }
];

const telefonoContacto = "2235246842";
const direccion = "Bolívar y Neuquén";

const requestBody = {
  header: { text: "🥶 MENÚ ANTIFRÍO x MENOS DE $5000 🥶" },
  body: { text: "🟢 Seleccioná tu plato para combatir el frío y escribí la cantidad que quieras" },
  footer: { text: `📲 Hacé tu pedido y\n📍 Retirá en ${direccion} o\n 🚚 Consultá el envío` },
  action: {
    list: {
      sections,
      label: " 🔺 Ver Menú "
    }
  },
  type: "list",
  to: remoteJid
};

return [{ json: requestBody }];
