// Este código SIEMPRE devuelve un array de objetos como requiere el sistema
function formatearProducto() {
  try {
    // Obtener los valores de las variables
    const title = $('Webhook').first().json.body.messages[0].reply.list_reply.title;
    const description = $('Webhook').first().json.body.messages[0].reply.list_reply.description;

    // DETECTAR SI ES COMIDA (por emoji o palabras clave)
    const esComida = title.includes('🥟') || title.includes('🍖') || title.includes('🥬') || 
                     title.includes('🍝') || title.includes('🍕') || title.includes('🍗') || 
                     title.includes('🥩') || title.includes('🥧') || title.includes('🍲') ||
                     title.includes('🥪') || title.includes('🍔') || title.includes('🥤') ||
                     title.includes('☕') || title.includes('🍮') || title.includes('🍽️');

    if (esComida) {
      // FORMATO PARA COMIDAS
      // Extraer precio del título (formato: "🥟 Empanada de carne - $1200")
      let precio = "";
      const precioTitleMatch = title.match(/\$\s*([\d,\.]+)/);
      if (precioTitleMatch && precioTitleMatch[1]) {
        precio = precioTitleMatch[1];
      }

      // Extraer nombre de la comida (quitar emoji y precio)
      let nombreComida = title.replace(/[\u{1F300}-\u{1F9FF}]/gu, '').replace(/\s*-\s*\$[\d,\.]+.*/, '').trim();

      const mensajeFormateado = `
🍽️ *${nombreComida}*
💰 Precio: *$${precio}*
📝 ${description}

¿Cuántas porciones querés?`;

      return mensajeFormateado;

    } else {
      // FORMATO ORIGINAL PARA PRODUCTOS (sin modificar)
      // Extraer los componentes individuales de la descripción
      // Precio
      let precio = "";
      const precioMatch = description.match(/Precio:\s*\*\$?([\d,\.]+)/);
      if (precioMatch && precioMatch[1]) {
        precio = precioMatch[1];
      }

      // Código
      let codigo = "";
      const codigoMatch = description.match(/Código:\s*(\w+)/);
      if (codigoMatch && codigoMatch[1]) {
        codigo = codigoMatch[1];
      }

      // Disponibilidad
      let disponibilidad = "🔴 Sin stock";
      if (description.includes("Disponible")) {
        disponibilidad = "🟢 Disponible";
      }

      // Construir el mensaje en el formato deseado
      const mensajeFormateado = `
. ${title}
.🔥 Precio: *$${precio}*
.🆔 Código: ${codigo}
.📦 Stock: ${disponibilidad}
`;

      return mensajeFormateado;
    }

  } catch (error) {
    console.error("Error al formatear producto:", error);
    return "Error al procesar producto";
  }
}

