// Funci√≥n mejorada para verificar si un texto contiene una palabra (considerando singular/plural)
function contienePalabra(texto, palabra) {
  if (!texto || !palabra) return false;

  try {
    // Limpiamos y normalizamos el texto y la palabra
    texto = (texto || "").toString().toLowerCase().trim();
    palabra = (palabra || "").toString().toLowerCase().trim();

    if (palabra === "") return false;

    // Considerar variantes (singular/plural y m√°s)
    let variantes = [palabra];

    // Plural a singular
    if (palabra.endsWith('s') && palabra.length > 2) {
      variantes.push(palabra.slice(0, -1)); // Quitar la 's' final
      // Casos especiales como "es" -> "e" (ej. "dentales" -> "dental")
      if (palabra.endsWith('es') && palabra.length > 3) {
        variantes.push(palabra.slice(0, -2));
      }
    } 
    // Singular a plural
    else {
      variantes.push(palabra + 's'); // A√±adir 's' al final
      // Casos especiales como "al" -> "ales"
      if (palabra.endsWith('al')) {
        variantes.push(palabra.slice(0, -1) + 'ales');
      }
    }

    // Log para debug
    // console.log(`Buscando variantes para "${palabra}":`, variantes);

    // Buscar palabra completa primero (priorizar coincidencias exactas)
    const palabraRegex = new RegExp(`(^|\\s|[^a-z√±√°√©√≠√≥√∫√º])${palabra}(\\s|[^a-z√±√°√©√≠√≥√∫√º]|$)`, 'i');
    if (palabraRegex.test(texto)) {
      return true;
    }

    // Si no hay coincidencia exacta, probar con las variantes
    return variantes.some(v => {
      if (v === palabra) return false; // Ya probamos con la palabra exacta

      try {
        const varianteRegex = new RegExp(`(^|\\s|[^a-z√±√°√©√≠√≥√∫√º])${v}(\\s|[^a-z√±√°√©√≠√≥√∫√º]|$)`, 'i');
        return varianteRegex.test(texto);
      } catch (e) {
        console.log(`Error en regex para variante "${v}":`, e.message);
        // Fallback - b√∫squeda simple
        return texto.includes(v);
      }
    });
  } catch (e) {
    console.log("Error en contienePalabra:", e.message);
    return false;
  }
}

// Funci√≥n mejorada para verificar si un producto contiene una palabra
function productoContienePalabra(producto, palabra) {
