    for (const [clave, valor] of Object.entries(producto)) {
      // Solo procesar valores de texto
      if (typeof valor === 'string' && valor.trim() !== '') {
        if (contienePalabra(valor, palabra)) {
          // console.log(`Coincidencia encontrada para "${palabra}" en campo "${clave}": "${valor}"`);
