    // Crear fecha interpret√°ndola como hora local de Argentina
    // NO usar new Date() directamente porque convierte a UTC
    const [fechaParte, horaParte] = fechaISO.split('T');
    const [a√±o, mes, dia] = fechaParte.split('-').map(Number);
    const [hora, minutos] = horaParte.split(':').map(Number);

    // Crear fecha en hora local (Argentina)
    const fechaLocal = new Date(a√±o, mes - 1, dia, hora, minutos, 0);

    // Formatear de vuelta a ISO pero manteniendo la hora local
    const fechaCorregida = fechaLocal.getFullYear() + '-' + 
      String(fechaLocal.getMonth() + 1).padStart(2, '0') + '-' + 
      String(fechaLocal.getDate()).padStart(2, '0') + 'T' + 
      String(fechaLocal.getHours()).padStart(2, '0') + ':' + 
      String(fechaLocal.getMinutes()).padStart(2, '0');

    console.log("Fecha corregida (hora local):", fechaCorregida);

