  if (typeof fechaCalendario === 'string' && fechaCalendario.includes('T')) {
    // Remover cualquier informaci√≥n de zona horaria (Z, +XX:XX, -XX:XX)
    const fechaSinZona = fechaCalendario.replace(/[Z]|[+-]\d{2}:\d{2}$/g, '');
