  const [fechaParte] = fechaISO.split('T');
  const [a√±o, mes, dia] = fechaParte.split('-').map(Number);

  // Formatear como DD-MM-YYYY (sin ceros a la izquierda)
  return `${dia}-${mes}-${a√±o}`;
