    // Para esto, necesitamos ajustar por la diferencia horaria
    const fechaBase = new Date(fechaISO);

    // Obtener offset de Argentina (-3 UTC)
    const offsetArgentina = -3; // Argentina es UTC-3
    const offsetLocal = fechaBase.getTimezoneOffset() / 60; // Offset local en horas

    // Calcular diferencia y ajustar
    const diferencia = offsetArgentina - (-offsetLocal);

