// --- Funciones de procesamiento de texto ---

// Funci√≥n para detectar si una l√≠nea contiene un producto o informaci√≥n con emojis
function isProductLine(line) {
  // Patrones de emojis comunes usados para mostrar productos
  const productEmojis = ['üì¶', 'üí∞', '‚úÖ', '‚ùå', 'üîß', 'üè≠', 'üí∏', 'üìÖ', 'üöö', '‚ö°', 'üìè', 'üî¢', 'üíµ', 'üõí', 'üìã', 'üè¶', '‚è≥', 'üë§', 'üì±', 'üîç', '‚è∞', 'üì∏', 'üè∑Ô∏è', 'üè™'];

  // Verificar si la l√≠nea contiene alguno de estos emojis
  return productEmojis.some(emoji => line.includes(emoji));
}

// Funci√≥n para detectar si estamos en una secci√≥n de productos/confirmaci√≥n
function isProductSection(lines, startIndex) {
  // Patrones que indican inicio de una secci√≥n de productos
  const productStartPatterns = [
    /encontr√©.*product/i,
    /tengo disponible/i,
    /opciones de/i,
    /confirmo tu/i,
    /confirmemos tu/i,
    /pedido confirmado/i,
    /encargo confirmado/i,
    /estado.*pedido/i,
    /voy a procesar/i,
    /opci√≥n \d+:/i,
    /producto \d+:/i,
    /totales:/i,
    /categor√≠as/i,
    /foto recibida/i
  ];

  // Verificar l√≠neas anteriores para contexto
  for (let i = Math.max(0, startIndex - 3); i <= startIndex; i++) {
    if (lines[i] && productStartPatterns.some(pattern => pattern.test(lines[i]))) {
      return true;
    }
  }

  return false;
}

// Funci√≥n para procesar el texto y dividirlo inteligentemente
function processAndSplitText(textInput) {
  // Aseg√∫rate de que textInput sea un string o pueda ser convertido
  let text = textInput;

  // Si la entrada no es un string
  if (typeof text !== 'string') {
    // Si es null o undefined, simplemente devolvemos un array vac√≠o
    if (text === null || text === undefined) {
      return [];
    }

    // Si es un objeto, intentamos encontrar campos comunes que contengan texto
    if (typeof text === 'object') {
      if (text.text) {
        text = text.text;
      } else if (text.message) {
        text = text.message;
      } else if (text.type === 'text' && text.text) {
        text = text.text;
      } else if (text.output) { // Intentamos extraer de un campo 'output'
          // Si output es un string, lo usamos
          if (typeof text.output === 'string') {
              text = text.output;
          } else { // Si output es un objeto o array, intentamos extraer de ah√≠
              const extracted = extractTextContent(text.output); // Usamos la funci√≥n de extracci√≥n
              if (extracted) {
                  text = extracted;
              } else {
                   // Si no pudimos extraer, intentamos convertir todo el objeto a string
                   try {
                       text = JSON.stringify(text);
                   } catch (e) {
                       console.error("No se pudo serializar el objeto a string:", e);
                       return []; // Si falla la serializaci√≥n, devolvemos vac√≠o
                   }
              }
          }
      } else {
        // Si no encontramos campos de texto comunes ni 'output', intentamos convertir a string
        try {
          text = JSON.stringify(text);
        } catch (e) {
           console.error("No se pudo serializar el objeto a string:", e);
           return []; // Si falla la serializaci√≥n, devolvemos vac√≠o
        }
      }
    } else { // Si no es string, objeto, null o undefined, devolvemos vac√≠o
       console.warn("Entrada a processAndSplitText no es string, objeto, null o undefined:", typeof text);
       return [];
    }
  }

  // Si despu√©s de los intentos no tenemos un string v√°lido, devolvemos vac√≠o
  if (typeof text !== 'string' || text.trim() === '') {
      return [];
  }

  // Ahora que tenemos un string, procesamos el formato
  const processedText = text
    .replace(/\*\*(.+?)\*\*/g, '*$1*') // **texto** -> *texto*
    .replace(/###\s*/g, '')         // Elimina encabezados ###
    .replace(/[¬°¬ø!]/g, '');         // Elimina signos de exclamaci√≥n e interrogaci√≥n iniciales y finales

  // Divide en l√≠neas para an√°lisis
  const lines = processedText.split('\n');

  // Grupos para almacenar los mensajes
  const messages = [];
  let currentMessage = [];
  let inProductSection = false;
  let inNumberedSection = false;
  let currentSectionNumber = null;

  // Detectar secciones y agrupables
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];

    // Verificar si estamos entrando en una secci√≥n de productos
    if (!inProductSection && (isProductLine(line) || isProductSection(lines, i))) {
      // Si tenemos contenido previo, lo guardamos
      if (currentMessage.length > 0) {
        messages.push(currentMessage.join('\n').trim());
        currentMessage = [];
      }
      inProductSection = true;
      inNumberedSection = false;
    }

    // Si estamos en una secci√≥n de productos
    if (inProductSection) {
      currentMessage.push(line);

      // Verificar si hemos salido de la secci√≥n de productos
      // (l√≠nea vac√≠a seguida de texto sin emojis de productos)
      if (line.trim() === '' && i + 1 < lines.length) {
        const nextLine = lines[i + 1].trim();
        // Si la siguiente l√≠nea no es vac√≠a y no tiene emojis de productos
        // y no es parte de una pregunta de confirmaci√≥n
        if (nextLine !== '' && !isProductLine(nextLine) && 
            !nextLine.includes('?') && !nextLine.toLowerCase().includes('confirm')) {
          // Terminamos la secci√≥n de productos
          messages.push(currentMessage.join('\n').trim());
          currentMessage = [];
          inProductSection = false;
        }
      }
    } else {
      // L√≥gica original para secciones no relacionadas con productos
      const numberedHeaderMatch = line.match(/^\s*(\d+)\.\s+([^:]+):/);

      if (numberedHeaderMatch) {
        const sectionNumber = parseInt(numberedHeaderMatch[1]);

        // Si estamos empezando una nueva secci√≥n numerada O si el n√∫mero no es el siguiente esperado
        if (!inNumberedSection || (inNumberedSection && sectionNumber !== currentSectionNumber + 1)) {
           // Si tenemos contenido previo, guardamos como mensaje separado
           if (currentMessage.length > 0) {
               messages.push(currentMessage.join('\n').trim());
               currentMessage = [];
           }
           inNumberedSection = true;
        }
        currentSectionNumber = sectionNumber;
        currentMessage.push(line);

      } else if (line.trim() === '') { // L√≠nea vac√≠a
          // Una l√≠nea vac√≠a puede terminar una secci√≥n si hay contenido previo
          if (currentMessage.length > 0) {
               // Si no estamos en una secci√≥n numerada, una l√≠nea vac√≠a termina el mensaje actual
               if (!inNumberedSection) {
                   messages.push(currentMessage.join('\n').trim());
                   currentMessage = [];
               } else {
                   // Si estamos en una secci√≥n numerada, una l√≠nea vac√≠a se agrega al mensaje actual
                   currentMessage.push(line);
               }
          }

      } else { // L√≠nea con contenido que no es un encabezado numerado
          currentMessage.push(line);
          inNumberedSection = false;
      }
    }
  }

  // Agregar el √∫ltimo mensaje si queda algo
  if (currentMessage.length > 0) {
    messages.push(currentMessage.join('\n').trim());
  }

  // Filtrar mensajes vac√≠os y limpiar l√≠neas vac√≠as extra
  return messages
    .filter(msg => msg.length > 0)
    .map(msg => {
      // Eliminar l√≠neas vac√≠as m√∫ltiples dentro del mensaje
      return msg.replace(/\n{3,}/g, '\n\n');
    });
}

// Funci√≥n para extraer texto de diferentes estructuras de datos
function extractTextContent(data) {
  // Si la data es null o undefined, retornamos null inmediatamente
  if (data === null || data === undefined) {
    return null;
  }

  // Si es un string, lo retornamos directamente
  if (typeof data === 'string') {
    return data;
  }

  // Si es un array
  if (Array.isArray(data)) {
    // Iteramos sobre el array e intentamos extraer texto de cada elemento
    for (const item of data) {
       const extracted = extractTextContent(item); // Llamada recursiva para elementos del array
       if (extracted) {
           return extracted; // Devolvemos el primer texto que encontramos
       }
    }
    return null; // Si no encontramos texto en ning√∫n elemento del array
  }

  // Si es un objeto
  if (typeof data === 'object') {
    // Priorizamos campos comunes que suelen contener texto
    if (data.text) {
      return data.text;
    }
    if (data.message) {
      return data.message;
    }
    // Si tiene un campo 'output', intentamos extraer texto de √©l (puede ser string, array u objeto)
    if (data.output !== undefined && data.output !== null) {
         const extracted = extractTextContent(data.output); // Llamada recursiva para el campo output
         if (extracted) {
             return extracted;
         }
    }
     // Si tiene un campo 'response', intentamos extraer texto de √©l
     if (data.response !== undefined && data.response !== null) {
         const extracted = extractTextContent(data.response); // Llamada recursiva para el campo response
         if (extracted) {
             return extracted;
         }
    }
     // Si tiene un campo 'json', intentamos extraer texto de √©l
     if (data.json !== undefined && data.json !== null) {
         const extracted = extractTextContent(data.json); // Llamada recursiva para el campo json
         if (extracted) {
             return extracted;
         }
    }
    // Si no encontramos campos de texto comunes ni estructuras anidadas esperadas,
    // intentamos convertir el objeto completo a string como √∫ltimo recurso
     try {
         return JSON.stringify(data);
     } catch (e) {
         console.error("No se pudo serializar el objeto a string en extractTextContent:", e);
         return null; // Si falla la serializaci√≥n
     }
  }

  // Si el tipo de dato no es manejado, retornamos null
  console.warn("Tipo de dato no manejado en extractTextContent:", typeof data);
  return null;
}

// --- L√≥gica Principal con manejo seguro de nodos ---
let textToProcess = null;

try {
  // Verificar si el nodo "Cancelar evento" existe y tiene datos
  let cancelarEventoData = null;
  try {
    // Usamos try-catch para capturar errores si el nodo no existe
    cancelarEventoData = $('Cancelar evento');
    if (cancelarEventoData && cancelarEventoData.first() && cancelarEventoData.first().json) {
      if (cancelarEventoData.first().json.response !== undefined) {
        textToProcess = extractTextContent(cancelarEventoData.first().json.response);
      } else {
        textToProcess = extractTextContent(cancelarEventoData.first().json);
      }
    }
  } catch (e) {
    // Silenciosamente ignoramos errores si el nodo no existe
    console.log("Nodo 'Cancelar evento' no disponible o sin datos v√°lidos");
  }

  // Solo intentamos acceder a AGE3 si a√∫n no hemos encontrado texto para procesar
  if (!textToProcess) {
    try {
      // Usamos try-catch para capturar errores si el nodo no existe
      const age3Data = $('AGE3');
      if (age3Data && age3Data.first() && age3Data.first().json) {
        if (age3Data.first().json.output !== undefined) {
          textToProcess = extractTextContent(age3Data.first().json.output);
        } else {
          textToProcess = extractTextContent(age3Data.first().json);
        }
      }
    } catch (e) {
      // Silenciosamente ignoramos errores si el nodo no existe
      console.log("Nodo 'AGE3' no disponible o sin datos v√°lidos");
    }
  }

  // Si no hemos encontrado datos en los nodos espec√≠ficos, intentamos con $input directamente
  if (!textToProcess) {
    try {
      const inputItems = $input.all();
      if (inputItems && inputItems.length > 0 && inputItems[0].json) {
        textToProcess = extractTextContent(inputItems[0].json);
      }
    } catch (e) {
      console.log("No se pudo acceder a los datos de entrada directamente:", e.message);
    }
  }

  // Procesar y retornar el resultado
  if (textToProcess) {
    const textArray = processAndSplitText(textToProcess);
    // Devolvemos un array con un objeto que contiene el array de texto
    return [{json: {text: textArray}}];
  } else {
    // Si no se pudo obtener ni procesar texto, devolvemos un array vac√≠o
    return [{json: {text: []}}];
  }
} catch (error) {
  // Capturamos cualquier error no manejado y devolvemos un objeto con informaci√≥n del error
  console.error("Error general en el nodo:", error.message);
  return [{json: {text: [], error: error.message}}];
}

// Sistema mejorado de combinaci√≥n de mensajes
const messagesBuffer = $json.allMessages || "";
const telefono = $json.msg?.telefono || $json.telefono;
const currentMsg = $json.msg || {};

// Funci√≥n auxiliar para parsear mensajes de forma segura
function safeParseMessage(msgStr) {
  try {
    if (!msgStr || typeof msgStr !== 'string') return null;
    const parsed = JSON.parse(msgStr.trim());
    return parsed && parsed.content ? parsed : null;
  } catch (e) {
    return null;
  }
}

// Procesar buffer de mensajes
let messages = [];

if (messagesBuffer) {
  // Separar mensajes y filtrar vac√≠os
  const messageStrings = messagesBuffer
    .split('|||')
    .filter(s => s && s.trim());

  // Parsear cada mensaje
  messages = messageStrings
    .map(safeParseMessage)
    .filter(msg => msg !== null);
}

// Si no hay mensajes v√°lidos, retornar mensaje vac√≠o
if (messages.length === 0) {
  return {
    msg: {
      telefono: telefono,
      content: currentMsg.content || "",
      type: currentMsg.type || "text",
      timestamp: new Date().toISOString(),
      idmsg: currentMsg.idmsg,
      nombre: currentMsg.nombre
    },
    isEmpty: true
  };
}

// Ordenar mensajes por timestamp
messages.sort((a, b) => {
  const timeA = a.timestamp ? new Date(a.timestamp).getTime() : 0;
  const timeB = b.timestamp ? new Date(b.timestamp).getTime() : 0;
  return timeA - timeB;
});

// Combinar contenidos
const combinedContent = messages
  .map(msg => msg.content)
  .filter(content => content && content.trim())
  .join(' ');

// Crear mensaje combinado con estructura correcta
return {
  msg: {
    telefono: telefono,
    content: combinedContent || "Sin contenido",
    type: "combined_text",
    timestamp: new Date().toISOString(),
    idmsg: messages[messages.length - 1]?.idmsg || currentMsg.idmsg,
    nombre: messages[0]?.nombre || currentMsg.nombre || "Usuario",
    button: messages[messages.length - 1]?.button || currentMsg.button || "",
    messageCount: messages.length
  },
  metadata: {
    firstMessageTime: messages[0]?.timestamp,
    lastMessageTime: messages[messages.length - 1]?.timestamp,
    originalMessages: messages.length <= 10 ? messages : messages.slice(-10)
  }
};

// Procesar mensaje individual cuando no estaba escribiendo
const msg = $('Extraer Datos Completos').first().json.msg || {};

// Validar que tengamos un mensaje v√°lido
if (!msg.content || !msg.telefono) {
  throw new Error('Mensaje inv√°lido: falta contenido o tel√©fono');
}

// Formatear mensaje individual con estructura correcta
return {
  msg: {
    telefono: msg.telefono,
    content: msg.content,
    type: msg.type || 'text',
    timestamp: msg.timestamp || new Date().toISOString(),
    idmsg: msg.idmsg,
    nombre: msg.nombre || 'Usuario',
    button: msg.button || '',
    messageCount: 1
  },
  metadata: {
    processedDirectly: true,
    wasTyping: false
  }
};
