// Funci√≥n para procesar el texto y dividirlo inteligentemente
function processAndSplitText(textInput) {
  // Aseg√∫rate de que textInput sea un string o pueda ser convertido
  let text = textInput;

  // Si la entrada no es un string
  if (typeof text !== 'string') {
    // Si es null o undefined, simplemente devolvemos un array vac√≠o
    if (text === null || text === undefined) {
      return [];
    }

    // Si es un objeto, intentamos encontrar campos comunes que contengan texto
    if (typeof text === 'object') {
      if (text.text) {
        text = text.text;
      } else if (text.message) {
        text = text.message;
      } else if (text.type === 'text' && text.text) {
        text = text.text;
      } else if (text.output) { // Intentamos extraer de un campo 'output'
          // Si output es un string, lo usamos
          if (typeof text.output === 'string') {
              text = text.output;
          } else { // Si output es un objeto o array, intentamos extraer de ah√≠
              const extracted = extractTextContent(text.output); // Usamos la funci√≥n de extracci√≥n
              if (extracted) {
                  text = extracted;
              } else {
                   // Si no pudimos extraer, intentamos convertir todo el objeto a string
                   try {
                       text = JSON.stringify(text);
                   } catch (e) {
                       console.error("No se pudo serializar el objeto a string:", e);
                       return []; // Si falla la serializaci√≥n, devolvemos vac√≠o
                   }
              }
          }
      } else {
        // Si no encontramos campos de texto comunes ni 'output', intentamos convertir a string
        try {
          text = JSON.stringify(text);
        } catch (e) {
           console.error("No se pudo serializar el objeto a string:", e);
           return []; // Si falla la serializaci√≥n, devolvemos vac√≠o
        }
      }
    } else { // Si no es string, objeto, null o undefined, devolvemos vac√≠o
       console.warn("Entrada a processAndSplitText no es string, objeto, null o undefined:", typeof text);
       return [];
    }
  }

  // Si despu√©s de los intentos no tenemos un string v√°lido, devolvemos vac√≠o
  if (typeof text !== 'string' || text.trim() === '') {
      return [];
  }

  // Ahora que tenemos un string, procesamos el formato
  const processedText = text
    .replace(/\*\*(.+?)\*\*/g, '*$1*') // **texto** -> *texto*
    .replace(/###\s*/g, '')         // Elimina encabezados ###
    .replace(/[¬°¬ø!]/g, '');         // Elimina signos de exclamaci√≥n e interrogaci√≥n iniciales y finales

  // Divide en l√≠neas para an√°lisis
  const lines = processedText.split('\n');

  // Grupos para almacenar los mensajes
  const messages = [];
  let currentMessage = [];
  let inNumberedSection = false;
  let currentSectionNumber = null;

  // Detectar secciones numeradas y agrupables
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    // Detecta si la l√≠nea es un encabezado numerado (ej: "1. Tipo de propiedad:")
    const numberedHeaderMatch = line.match(/^\s*(\d+)\.\s+([^:]+):/);

    if (numberedHeaderMatch) {
      const sectionNumber = parseInt(numberedHeaderMatch[1]);

      // Si estamos empezando una nueva secci√≥n numerada O si el n√∫mero no es el siguiente esperado
      if (!inNumberedSection || (inNumberedSection && sectionNumber !== currentSectionNumber + 1)) {
         // Si tenemos contenido previo, guardamos como mensaje separado
         if (currentMessage.length > 0) {
             messages.push(currentMessage.join('\n').trim()); // Trim antes de guardar
             currentMessage = [];
         }
         inNumberedSection = true;
      }
      currentSectionNumber = sectionNumber; // Actualizamos el n√∫mero de secci√≥n actual
       currentMessage.push(line); // Agregamos la l√≠nea al mensaje actual

    } else if (line.trim() === '') { // L√≠nea vac√≠a
        // Una l√≠nea vac√≠a puede terminar una secci√≥n si hay contenido previo
        if (currentMessage.length > 0) {
             // Si no estamos en una secci√≥n numerada, una l√≠nea vac√≠a termina el mensaje actual
             if (!inNumberedSection) {
                 messages.push(currentMessage.join('\n').trim()); // Trim antes de guardar
                 currentMessage = [];
             } else {
                 // Si estamos en una secci√≥n numerada, una l√≠nea vac√≠a se agrega al mensaje actual,
                 // podr√≠a terminar la secci√≥n si hay otra l√≠nea vac√≠a o fin de texto despu√©s.
                 currentMessage.push(line);
             }
        }
         // Si currentMessage est√° vac√≠o, una l√≠nea vac√≠a consecutiva no hace nada

    } else { // L√≠nea con contenido que no es un encabezado numerado
        currentMessage.push(line);
        inNumberedSection = false; // Salimos de la secci√≥n numerada si el contenido no sigue el patr√≥n
    }
  }

  // Agregar el √∫ltimo mensaje si queda algo
  if (currentMessage.length > 0) {
    messages.push(currentMessage.join('\n').trim()); // Trim antes de guardar
  }

  // Filtrar mensajes vac√≠os y limpiar l√≠neas vac√≠as extra
  return messages
    .filter(msg => msg.length > 0) // Filtrar cadenas vac√≠as despu√©s del trim
    .map(msg => {
      // Eliminar l√≠neas vac√≠as m√∫ltiples dentro del mensaje
      return msg.replace(/\n{2,}/g, '\n\n');
    });
}

// Funci√≥n para extraer texto de diferentes estructuras de datos
function extractTextContent(data) {
  // Si la data es null o undefined, retornamos null inmediatamente
  if (data === null || data === undefined) {
    return null;
  }

  // Si es un string, lo retornamos directamente
  if (typeof data === 'string') {
    return data;
  }

  // Si es un array
  if (Array.isArray(data)) {
    // Iteramos sobre el array e intentamos extraer texto de cada elemento
    for (const item of data) {
       const extracted = extractTextContent(item); // Llamada recursiva para elementos del array
       if (extracted) {
           return extracted; // Devolvemos el primer texto que encontramos
       }
    }
    return null; // Si no encontramos texto en ning√∫n elemento del array
  }

  // Si es un objeto
  if (typeof data === 'object') {
    // Priorizamos campos comunes que suelen contener texto
    if (data.text) {
      return data.text;
    }
    if (data.message) {
      return data.message;
    }
    // Si tiene un campo 'output', intentamos extraer texto de √©l (puede ser string, array u objeto)
    if (data.output !== undefined && data.output !== null) {
         const extracted = extractTextContent(data.output); // Llamada recursiva para el campo output
         if (extracted) {
             return extracted;
         }
    }
     // Si tiene un campo 'response', intentamos extraer texto de √©l
     if (data.response !== undefined && data.response !== null) {
         const extracted = extractTextContent(data.response); // Llamada recursiva para el campo response
         if (extracted) {
             return extracted;
         }
    }
     // Si tiene un campo 'json', intentamos extraer texto de √©l
     if (data.json !== undefined && data.json !== null) {
         const extracted = extractTextContent(data.json); // Llamada recursiva para el campo json
         if (extracted) {
             return extracted;
         }
    }
    // Si no encontramos campos de texto comunes ni estructuras anidadas esperadas,
    // intentamos convertir el objeto completo a string como √∫ltimo recurso
     try {
         return JSON.stringify(data);
     } catch (e) {
         console.error("No se pudo serializar el objeto a string en extractTextContent:", e);
         return null; // Si falla la serializaci√≥n
     }
  }

  // Si el tipo de dato no es manejado, retornamos null
  console.warn("Tipo de dato no manejado en extractTextContent:", typeof data);
  return null;
}

