// --- Funciones de procesamiento de texto ---

// Funci√≥n para dividir texto en fragmentos con tiempo de lectura
function splitTextIntoChunks(text, maxChars = 200) {
  // Si el texto es muy corto, devolvemos un solo fragmento
  if (!text || text.length <= maxChars) {
    return [{
      parte: 1,
      texto: text || '',
      time: calculateReadingTime(text || '')
    }];
  }

  const chunks = [];
  let currentChunk = '';
  let partNumber = 1;

  // Dividimos por palabras para no cortar palabras a la mitad
  const words = text.split(' ');

  for (let i = 0; i < words.length; i++) {
    const word = words[i];
    const potentialChunk = currentChunk ? currentChunk + ' ' + word : word;

    // Si agregar la siguiente palabra excede el l√≠mite
    if (potentialChunk.length > maxChars) {
      // Si la palabra sola es m√°s larga que maxChars, la cortamos
      if (word.length > maxChars) {
        // Guardamos el chunk actual si tiene contenido
        if (currentChunk) {
          chunks.push({
            parte: partNumber++,
            texto: currentChunk.trim(),
            time: calculateReadingTime(currentChunk.trim())
          });
        }

        // Cortamos la palabra larga en pedazos
        let remainingWord = word;
        while (remainingWord.length > maxChars) {
          chunks.push({
            parte: partNumber++,
            texto: remainingWord.substring(0, maxChars),
            time: calculateReadingTime(remainingWord.substring(0, maxChars))
          });
          remainingWord = remainingWord.substring(maxChars);
        }

        // El resto de la palabra se convierte en el nuevo currentChunk
        currentChunk = remainingWord;
      } else {
        // Guardamos el chunk actual y empezamos uno nuevo con esta palabra
        chunks.push({
          parte: partNumber++,
          texto: currentChunk.trim(),
          time: calculateReadingTime(currentChunk.trim())
        });
        currentChunk = word;
      }
    } else {
      // Si cabe, agregamos la palabra al chunk actual
      currentChunk = potentialChunk;
    }
  }

  // Agregar el √∫ltimo chunk si queda algo
  if (currentChunk.trim()) {
    chunks.push({
      parte: partNumber,
      texto: currentChunk.trim(),
      time: calculateReadingTime(currentChunk.trim())
    });
  }

  return chunks;
}

// Funci√≥n para calcular el tiempo de lectura basado en la longitud del texto
function calculateReadingTime(text) {
  const length = text.length;

  if (length > 150) {
    return 4; // segundos
  } else if (length >= 50) {
    return 3; // segundos
  } else {
    return 1; // segundo
  }
}

// Funci√≥n para determinar si un texto necesita ser dividido
function needsSplitting(text) {
  if (!text || typeof text !== 'string') return false;

  // Criterios: m√°s de 500 caracteres o m√°s de 100 palabras
  const charCount = text.length;
  const wordCount = text.split(/\s+/).filter(word => word.length > 0).length;

  return charCount > 500 || wordCount > 100;
}

// Funci√≥n para procesar el texto y dividirlo inteligentemente (funci√≥n original mejorada)
function processAndSplitText(textInput) {
  // Aseg√∫rate de que textInput sea un string o pueda ser convertido
  let text = textInput;

  // Si la entrada no es un string
  if (typeof text !== 'string') {
    // Si es null o undefined, simplemente devolvemos un array vac√≠o
    if (text === null || text === undefined) {
      return [];
    }

    // Si es un objeto, intentamos encontrar campos comunes que contengan texto
    if (typeof text === 'object') {
      if (text.text) {
        text = text.text;
      } else if (text.message) {
        text = text.message;
      } else if (text.type === 'text' && text.text) {
        text = text.text;
      } else if (text.output) {
        // Si output es un string, lo usamos
        if (typeof text.output === 'string') {
          text = text.output;
        } else {
          // Si output es un objeto o array, intentamos extraer de ah√≠
          const extracted = extractTextContent(text.output);
          if (extracted) {
            text = extracted;
          } else {
            // Si no pudimos extraer, intentamos convertir todo el objeto a string
            try {
              text = JSON.stringify(text);
            } catch (e) {
              console.error("No se pudo serializar el objeto a string:", e);
              return [];
            }
          }
        }
      } else {
        // Si no encontramos campos de texto comunes ni 'output', intentamos convertir a string
        try {
          text = JSON.stringify(text);
        } catch (e) {
          console.error("No se pudo serializar el objeto a string:", e);
          return [];
        }
      }
    } else {
      // Si no es string, objeto, null o undefined, devolvemos vac√≠o
      console.warn("Entrada a processAndSplitText no es string, objeto, null o undefined:", typeof text);
      return [];
    }
  }

  // Si despu√©s de los intentos no tenemos un string v√°lido, devolvemos vac√≠o
  if (typeof text !== 'string' || text.trim() === '') {
    return [];
  }

  // Ahora que tenemos un string, procesamos el formato
  const processedText = text
    .replace(/\*\*(.+?)\*\*/g, '*$1*') // **texto** -> *texto*
    .replace(/###\s*/g, '')         // Elimina encabezados ###
    .replace(/[¬°¬ø!]/g, '');         // Elimina signos de exclamaci√≥n e interrogaci√≥n iniciales y finales

  // Divide en l√≠neas para an√°lisis
  const lines = processedText.split('\n');

  // Grupos para almacenar los mensajes
  const messages = [];
  let currentMessage = [];
  let inNumberedSection = false;
  let currentSectionNumber = null;

  // Detectar secciones numeradas y agrupables
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    // Detecta si la l√≠nea es un encabezado numerado (ej: "1. Tipo de propiedad:")
    const numberedHeaderMatch = line.match(/^\s*(\d+)\.\s+([^:]+):/);

    if (numberedHeaderMatch) {
      const sectionNumber = parseInt(numberedHeaderMatch[1]);

      // Si estamos empezando una nueva secci√≥n numerada O si el n√∫mero no es el siguiente esperado
      if (!inNumberedSection || (inNumberedSection && sectionNumber !== currentSectionNumber + 1)) {
        // Si tenemos contenido previo, guardamos como mensaje separado
        if (currentMessage.length > 0) {
          messages.push(currentMessage.join('\n').trim());
          currentMessage = [];
        }
        inNumberedSection = true;
      }
      currentSectionNumber = sectionNumber;
      currentMessage.push(line);

    } else if (line.trim() === '') {
      // Una l√≠nea vac√≠a puede terminar una secci√≥n si hay contenido previo
      if (currentMessage.length > 0) {
        // Si no estamos en una secci√≥n numerada, una l√≠nea vac√≠a termina el mensaje actual
        if (!inNumberedSection) {
          messages.push(currentMessage.join('\n').trim());
          currentMessage = [];
        } else {
          // Si estamos en una secci√≥n numerada, una l√≠nea vac√≠a se agrega al mensaje actual
          currentMessage.push(line);
        }
      }

    } else {
      // L√≠nea con contenido que no es un encabezado numerado
      currentMessage.push(line);
      inNumberedSection = false;
    }
  }

  // Agregar el √∫ltimo mensaje si queda algo
  if (currentMessage.length > 0) {
    messages.push(currentMessage.join('\n').trim());
  }

  // Filtrar mensajes vac√≠os y limpiar l√≠neas vac√≠as extra
  return messages
    .filter(msg => msg.length > 0)
    .map(msg => {
      // Eliminar l√≠neas vac√≠as m√∫ltiples dentro del mensaje
      return msg.replace(/\n{2,}/g, '\n\n');
    });
}

// Funci√≥n para extraer texto de diferentes estructuras de datos
function extractTextContent(data) {
  // Si la data es null o undefined, retornamos null inmediatamente
  if (data === null || data === undefined) {
    return null;
  }

  // Si es un string, lo retornamos directamente
  if (typeof data === 'string') {
    return data;
  }

  // Si es un array
  if (Array.isArray(data)) {
    // Iteramos sobre el array e intentamos extraer texto de cada elemento
    for (const item of data) {
      const extracted = extractTextContent(item);
      if (extracted) {
        return extracted;
      }
    }
    return null;
  }

  // Si es un objeto
  if (typeof data === 'object') {
    // Priorizamos campos comunes que suelen contener texto
    if (data.text) {
      return data.text;
    }
    if (data.message) {
      return data.message;
    }
    // Si tiene un campo 'output', intentamos extraer texto de √©l
    if (data.output !== undefined && data.output !== null) {
      const extracted = extractTextContent(data.output);
      if (extracted) {
        return extracted;
      }
    }
    // Si tiene un campo 'response', intentamos extraer texto de √©l
    if (data.response !== undefined && data.response !== null) {
      const extracted = extractTextContent(data.response);
      if (extracted) {
        return extracted;
      }
    }
    // Si tiene un campo 'json', intentamos extraer texto de √©l
    if (data.json !== undefined && data.json !== null) {
      const extracted = extractTextContent(data.json);
      if (extracted) {
        return extracted;
      }
    }
    // Si no encontramos campos de texto comunes ni estructuras anidadas esperadas,
    // intentamos convertir el objeto completo a string como √∫ltimo recurso
    try {
      return JSON.stringify(data);
    } catch (e) {
      console.error("No se pudo serializar el objeto a string en extractTextContent:", e);
      return null;
    }
  }

  // Si el tipo de dato no es manejado, retornamos null
  console.warn("Tipo de dato no manejado en extractTextContent:", typeof data);
  return null;
}

// --- L√≥gica Principal con manejo seguro de nodos ---
let textToProcess = null;

try {
  // Verificar si el nodo "Cancelar evento" existe y tiene datos
  let cancelarEventoData = null;
  try {
    // Usamos try-catch para capturar errores si el nodo no existe
    cancelarEventoData = $('Cancelar evento');
    if (cancelarEventoData && cancelarEventoData.first() && cancelarEventoData.first().json) {
      if (cancelarEventoData.first().json.response !== undefined) {
        textToProcess = extractTextContent(cancelarEventoData.first().json.response);
      } else {
        textToProcess = extractTextContent(cancelarEventoData.first().json);
      }
    }
  } catch (e) {
    // Silenciosamente ignoramos errores si el nodo no existe
    console.log("Nodo 'Cancelar evento' no disponible o sin datos v√°lidos");
  }

  // Solo intentamos acceder a AGE3 si a√∫n no hemos encontrado texto para procesar
  if (!textToProcess) {
    try {
      // Usamos try-catch para capturar errores si el nodo no existe
      const age3Data = $('AGE3');
      if (age3Data && age3Data.first() && age3Data.first().json) {
        if (age3Data.first().json.output !== undefined) {
          textToProcess = extractTextContent(age3Data.first().json.output);
        } else {
          textToProcess = extractTextContent(age3Data.first().json);
        }
      }
    } catch (e) {
      // Silenciosamente ignoramos errores si el nodo no existe
      console.log("Nodo 'AGE3' no disponible o sin datos v√°lidos");
    }
  }

  // Si no hemos encontrado datos en los nodos espec√≠ficos, intentamos con $input directamente
  if (!textToProcess) {
    try {
      const inputItems = $input.all();
      if (inputItems && inputItems.length > 0 && inputItems[0].json) {
        textToProcess = extractTextContent(inputItems[0].json);
      }
    } catch (e) {
      console.log("No se pudo acceder a los datos de entrada directamente:", e.message);
    }
  }

  // Procesar y retornar el resultado
  if (textToProcess) {
    const textArray = processAndSplitText(textToProcess);

    // Procesar cada mensaje y dividirlo en chunks si es necesario
    const processedMessages = [];

    for (const message of textArray) {
      if (needsSplitting(message)) {
        // Si el mensaje es largo, lo dividimos en chunks
        const chunks = splitTextIntoChunks(message);
        processedMessages.push(...chunks);
      } else {
        // Si el mensaje es corto, lo agregamos tal cual con tiempo de lectura
        processedMessages.push({
          parte: processedMessages.length + 1,
          texto: message,
          time: calculateReadingTime(message)
        });
      }
    }

    // Re-numerar las partes para que sean consecutivas
    processedMessages.forEach((msg, index) => {
      msg.parte = index + 1;
    });

    // Devolvemos la estructura con los mensajes procesados
    return [{json: {messages: processedMessages, totalParts: processedMessages.length}}];
  } else {
    // Si no se pudo obtener ni procesar texto, devolvemos un array vac√≠o
    return [{json: {messages: [], totalParts: 0}}];
  }
} catch (error) {
  // Capturamos cualquier error no manejado y devolvemos un objeto con informaci√≥n del error
  console.error("Error general en el nodo:", error.message);
  return [{json: {messages: [], totalParts: 0, error: error.message}}];
}

// OBTENER EL TEXTO DEL MENSAJE
const messageText = $input.first().json.msg.content;

// FUNCI√ìN PARA LIMPIAR TEXTO
function cleanText(text) {
  return text.replace(/\s+/g, ' ').trim();
}

// FUNCI√ìN PARA FORMATEAR FECHA EN FORMATO ISO (YYYY-MM-DD)
function formatFecha(fecha) {
  try {
    const fechaLimpia = cleanText(fecha);

    // Si la fecha est√° en formato DD/MM/YYYY o DD-MM-YYYY
    if (fechaLimpia.match(/^\d{1,2}[\/\-]\d{1,2}[\/\-]\d{4}$/)) {
      const parts = fechaLimpia.split(/[\/\-]/);
      const day = parts[0].padStart(2, '0');
      const month = parts[1].padStart(2, '0');
      const year = parts[2];
      return `${year}-${month}-${day}`; // Formato ISO para NocoDB
    }
    // Si la fecha est√° en formato YYYY-MM-DD
    else if (fechaLimpia.match(/^\d{4}[\/\-]\d{1,2}[\/\-]\d{1,2}$/)) {
      const parts = fechaLimpia.split(/[\/\-]/);
      const year = parts[0];
      const month = parts[1].padStart(2, '0');
      const day = parts[2].padStart(2, '0');
      return `${year}-${month}-${day}`;
    }
    else {
      // Intentar parsear con Date y convertir a ISO
      const tempDate = new Date(fechaLimpia);
      if (!isNaN(tempDate.getTime())) {
        const year = tempDate.getFullYear();
        const month = (tempDate.getMonth() + 1).toString().padStart(2, '0');
        const day = tempDate.getDate().toString().padStart(2, '0');
        return `${year}-${month}-${day}`;
      }
      return fechaLimpia;
    }
  } catch (error) {
    return fecha;
  }
}

// FUNCI√ìN PARA FORMATEAR HORA EN FORMATO 24H (HH:MM:SS)
function formatHora(hora) {
  try {
    const horaLimpia = cleanText(hora);

    // Si la hora est√° en formato HH:MM (24h) o H:MM
    if (horaLimpia.match(/^\d{1,2}:\d{2}$/)) {
      const timeParts = horaLimpia.split(':');
      const hours = timeParts[0].padStart(2, '0');
      const minutes = timeParts[1].padStart(2, '0');
      return `${hours}:${minutes}:00`; // Agregar segundos para formato completo
    }
    // Si la hora incluye segundos HH:MM:SS
    else if (horaLimpia.match(/^\d{1,2}:\d{2}:\d{2}$/)) {
      const timeParts = horaLimpia.split(':');
      const hours = timeParts[0].padStart(2, '0');
      const minutes = timeParts[1].padStart(2, '0');
      const seconds = timeParts[2].padStart(2, '0');
      return `${hours}:${minutes}:${seconds}`;
    }
    // Si la hora incluye AM/PM, convertir a 24h
    else if (horaLimpia.match(/^\d{1,2}:\d{2}\s*(AM|PM)$/i)) {
      const [time, period] = horaLimpia.split(/\s+/);
      const [hours, minutes] = time.split(':');
      let hour24 = parseInt(hours);

      if (period.toUpperCase() === 'PM' && hour24 !== 12) {
        hour24 += 12;
      } else if (period.toUpperCase() === 'AM' && hour24 === 12) {
        hour24 = 0;
      }

      return `${hour24.toString().padStart(2, '0')}:${minutes}:00`;
    }
    else {
      // Si no coincide con ning√∫n formato, asumir que son solo horas y agregar :00:00
      if (horaLimpia.match(/^\d{1,2}$/)) {
        return `${horaLimpia.padStart(2, '0')}:00:00`;
      }
      return horaLimpia; // Retornar como est√°
    }
  } catch (error) {
    return hora; // Retornar original en caso de error
  }
}

