// EXTRAER DATOS ESPEC√çFICOS
function parseMessage(text) {
  let nombre = '';
  let fecha = '';
  let hora = '';
  let servicio = '';

  // Verificar si es un mensaje de confirmaci√≥n
  if (text.includes('‚úÖ CONFIRMADO')) {
    // Extraer datos del formato: ‚úÖ CONFIRMADO - Juan P√©rez | 30/07/2025 | 15:30 | Consulta General
    const parts = text.split(' - ')[1].split(' | ');
    if (parts.length === 4) {
      nombre = cleanText(parts[0]);
      fecha = formatFecha(parts[1]);
      hora = formatHora(parts[2]);
      servicio = cleanText(parts[3]);
    }
  } else {
    // Buscar NOMBRE (puede estar como "Paciente:", "Nombre:", "Cliente:")
    const nombreMatch = text.match(/(?:Paciente|Nombre|Cliente):\s*([^üìßüìÖ‚è∞üè•üí°\n|]+)/i);
    if (nombreMatch) nombre = cleanText(nombreMatch[1]);

    // Buscar FECHA
    const fechaMatch = text.match(/(?:Fecha):\s*([^üìßüìÖ‚è∞üè•üí°\n|]+)/i);
    if (fechaMatch) fecha = formatFecha(fechaMatch[1]);

    // Buscar HORA
    const horaMatch = text.match(/(?:Hora):\s*([^üìßüìÖ‚è∞üè•üí°\n|]+)/i);
    if (horaMatch) hora = formatHora(horaMatch[1]);

    // Buscar SERVICIO (puede estar como "Motivo:", "Servicio:", "Consulta:")
    const servicioMatch = text.match(/(?:Motivo|Servicio|Consulta|Tipo):\s*([^üìßüìÖ‚è∞üè•üí°\n|]+)/i);
    if (servicioMatch) servicio = cleanText(servicioMatch[1]);
  }

  return {
    NOMBRE: nombre,
    FECHA: fecha,
    HORA: hora,
    SERVICIO: servicio,
    ID: obtenerID()
  };
}

// PROCESAR Y RETORNAR LOS DATOS ESPEC√çFICOS
