  NOMBRE: datosExtraidos.nombre,
  ID_USUARIO: datosInput.id,
  FECHA: datosExtraidos.fecha, // Ahora contiene fecha y hora: "2025-08-01T10:00"
  SERVICIO: datosExtraidos.servicio,
  CONFIRMACION_CITA: messageText, // El mensaje completo
  TELEFONO: datosInput.telefono
};

// LOG PARA DEBUGGING
console.log('Mensaje recibido:', messageText);
console.log('Datos extra√≠dos:', resultado);

return [{ json: resultado }];

