// --- Funciones de procesamiento de texto ---

// Función para dividir texto en fragmentos con tiempo de lectura
function splitTextIntoChunks(text, maxChars = 200) {
  // Si el texto es muy corto, devolvemos un solo fragmento
  if (!text || text.length <= maxChars) {
    return [{
      parte: 1,
      texto: text || '',
      time: calculateReadingTime(text || '')
    }];
  }

  const chunks = [];
  let currentChunk = '';
  let partNumber = 1;

  // Dividimos por palabras para no cortar palabras a la mitad
  const words = text.split(' ');

  for (let i = 0; i < words.length; i++) {
    const word = words[i];
    const potentialChunk = currentChunk ? currentChunk + ' ' + word : word;

    // Si agregar la siguiente palabra excede el límite
    if (potentialChunk.length > maxChars) {
      // Si la palabra sola es más larga que maxChars, la cortamos
      if (word.length > maxChars) {
        // Guardamos el chunk actual si tiene contenido
        if (currentChunk) {
          chunks.push({
            parte: partNumber++,
            texto: currentChunk.trim(),
            time: calculateReadingTime(currentChunk.trim())
          });
        }

        // Cortamos la palabra larga en pedazos
        let remainingWord = word;
        while (remainingWord.length > maxChars) {
          chunks.push({
            parte: partNumber++,
            texto: remainingWord.substring(0, maxChars),
            time: calculateReadingTime(remainingWord.substring(0, maxChars))
          });
          remainingWord = remainingWord.substring(maxChars);
        }

        // El resto de la palabra se convierte en el nuevo currentChunk
        currentChunk = remainingWord;
      } else {
        // Guardamos el chunk actual y empezamos uno nuevo con esta palabra
        chunks.push({
          parte: partNumber++,
          texto: currentChunk.trim(),
          time: calculateReadingTime(currentChunk.trim())
        });
        currentChunk = word;
      }
    } else {
      // Si cabe, agregamos la palabra al chunk actual
      currentChunk = potentialChunk;
    }
  }

  // Agregar el último chunk si queda algo
  if (currentChunk.trim()) {
    chunks.push({
      parte: partNumber,
      texto: currentChunk.trim(),
      time: calculateReadingTime(currentChunk.trim())
    });
  }

  return chunks;
}

// Función para calcular el tiempo de lectura basado en la longitud del texto
function calculateReadingTime(text) {
  const length = text.length;

  if (length > 150) {
    return 4; // segundos
  } else if (length >= 50) {
    return 3; // segundos
  } else {
    return 1; // segundo
  }
}

// Función para determinar si un texto necesita ser dividido
function needsSplitting(text) {
  if (!text || typeof text !== 'string') return false;

  // Criterios: más de 500 caracteres o más de 100 palabras
  const charCount = text.length;
  const wordCount = text.split(/\s+/).filter(word => word.length > 0).length;

  return charCount > 500 || wordCount > 100;
}

// Función para procesar el texto y dividirlo inteligentemente (función original mejorada)
function processAndSplitText(textInput) {
  // Asegúrate de que textInput sea un string o pueda ser convertido
  let text = textInput;

  // Si la entrada no es un string
  if (typeof text !== 'string') {
    // Si es null o undefined, simplemente devolvemos un array vacío
    if (text === null || text === undefined) {
      return [];
    }

    // Si es un objeto, intentamos encontrar campos comunes que contengan texto
    if (typeof text === 'object') {
      if (text.text) {
        text = text.text;
      } else if (text.message) {
        text = text.message;
      } else if (text.type === 'text' && text.text) {
        text = text.text;
      } else if (text.output) {
        // Si output es un string, lo usamos
        if (typeof text.output === 'string') {
          text = text.output;
        } else {
          // Si output es un objeto o array, intentamos extraer de ahí
          const extracted = extractTextContent(text.output);
          if (extracted) {
            text = extracted;
          } else {
            // Si no pudimos extraer, intentamos convertir todo el objeto a string
            try {
              text = JSON.stringify(text);
            } catch (e) {
              console.error("No se pudo serializar el objeto a string:", e);
              return [];
            }
          }
        }
      } else {
        // Si no encontramos campos de texto comunes ni 'output', intentamos convertir a string
        try {
          text = JSON.stringify(text);
        } catch (e) {
          console.error("No se pudo serializar el objeto a string:", e);
          return [];
        }
      }
    } else {
      // Si no es string, objeto, null o undefined, devolvemos vacío
      console.warn("Entrada a processAndSplitText no es string, objeto, null o undefined:", typeof text);
      return [];
    }
  }

  // Si después de los intentos no tenemos un string válido, devolvemos vacío
  if (typeof text !== 'string' || text.trim() === '') {
    return [];
  }

  // Ahora que tenemos un string, procesamos el formato
  const processedText = text
    .replace(/\*\*(.+?)\*\*/g, '*$1*') // **texto** -> *texto*
    .replace(/###\s*/g, '')         // Elimina encabezados ###
    .replace(/[¡¿!]/g, '');         // Elimina signos de exclamación e interrogación iniciales y finales

  // Divide en líneas para análisis
  const lines = processedText.split('\n');

  // Grupos para almacenar los mensajes
  const messages = [];
  let currentMessage = [];
  let inNumberedSection = false;
  let currentSectionNumber = null;

  // Detectar secciones numeradas y agrupables
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    // Detecta si la línea es un encabezado numerado (ej: "1. Tipo de propiedad:")
    const numberedHeaderMatch = line.match(/^\s*(\d+)\.\s+([^:]+):/);

    if (numberedHeaderMatch) {
      const sectionNumber = parseInt(numberedHeaderMatch[1]);

      // Si estamos empezando una nueva sección numerada O si el número no es el siguiente esperado
      if (!inNumberedSection || (inNumberedSection && sectionNumber !== currentSectionNumber + 1)) {
        // Si tenemos contenido previo, guardamos como mensaje separado
        if (currentMessage.length > 0) {
          messages.push(currentMessage.join('\n').trim());
          currentMessage = [];
        }
        inNumberedSection = true;
      }
      currentSectionNumber = sectionNumber;
      currentMessage.push(line);

    } else if (line.trim() === '') {
      // Una línea vacía puede terminar una sección si hay contenido previo
      if (currentMessage.length > 0) {
        // Si no estamos en una sección numerada, una línea vacía termina el mensaje actual
        if (!inNumberedSection) {
          messages.push(currentMessage.join('\n').trim());
          currentMessage = [];
        } else {
          // Si estamos en una sección numerada, una línea vacía se agrega al mensaje actual
          currentMessage.push(line);
        }
      }

    } else {
      // Línea con contenido que no es un encabezado numerado
      currentMessage.push(line);
      inNumberedSection = false;
    }
  }

  // Agregar el último mensaje si queda algo
  if (currentMessage.length > 0) {
    messages.push(currentMessage.join('\n').trim());
  }

  // Filtrar mensajes vacíos y limpiar líneas vacías extra
  return messages
    .filter(msg => msg.length > 0)
    .map(msg => {
      // Eliminar líneas vacías múltiples dentro del mensaje
      return msg.replace(/\n{2,}/g, '\n\n');
    });
}

// Función para extraer texto de diferentes estructuras de datos
function extractTextContent(data) {
  // Si la data es null o undefined, retornamos null inmediatamente
  if (data === null || data === undefined) {
    return null;
  }

  // Si es un string, lo retornamos directamente
  if (typeof data === 'string') {
    return data;
  }

  // Si es un array
  if (Array.isArray(data)) {
    // Iteramos sobre el array e intentamos extraer texto de cada elemento
    for (const item of data) {
      const extracted = extractTextContent(item);
      if (extracted) {
        return extracted;
      }
    }
    return null;
  }

  // Si es un objeto
  if (typeof data === 'object') {
    // Priorizamos campos comunes que suelen contener texto
    if (data.text) {
      return data.text;
    }
    if (data.message) {
      return data.message;
    }
    // Si tiene un campo 'output', intentamos extraer texto de él
    if (data.output !== undefined && data.output !== null) {
      const extracted = extractTextContent(data.output);
      if (extracted) {
        return extracted;
      }
    }
    // Si tiene un campo 'response', intentamos extraer texto de él
    if (data.response !== undefined && data.response !== null) {
      const extracted = extractTextContent(data.response);
      if (extracted) {
        return extracted;
      }
    }
    // Si tiene un campo 'json', intentamos extraer texto de él
    if (data.json !== undefined && data.json !== null) {
      const extracted = extractTextContent(data.json);
      if (extracted) {
        return extracted;
      }
    }
    // Si no encontramos campos de texto comunes ni estructuras anidadas esperadas,
    // intentamos convertir el objeto completo a string como último recurso
    try {
      return JSON.stringify(data);
    } catch (e) {
      console.error("No se pudo serializar el objeto a string en extractTextContent:", e);
      return null;
    }
  }

  // Si el tipo de dato no es manejado, retornamos null
  console.warn("Tipo de dato no manejado en extractTextContent:", typeof data);
  return null;
}

