// Función para convertir nombre de mes a número
function mesANumero(nombreMes) {
  const meses = {
    'enero': '01', 'febrero': '02', 'marzo': '03', 'abril': '04',
    'mayo': '05', 'junio': '06', 'julio': '07', 'agosto': '08',
    'septiembre': '09', 'octubre': '10', 'noviembre': '11', 'diciembre': '12'
  };
  return meses[nombreMes.toLowerCase()] || '01';
}

// Función para extraer fecha ISO del título
function extraerFechaISODelTitulo(title) {
  // Ejemplo: "📅 30 de Mayo : 12:00 a 12:30"
  const regex = /📅\s*(\d+)\s*de\s*(\w+)\s*:\s*(\d{2}):(\d{2})/i;
  const match = title.match(regex);

  if (match) {
    const dia = match[1].padStart(2, '0'); // "30" -> "30"
    const mes = mesANumero(match[2]); // "Mayo" -> "05"
    const hora = match[3]; // "12"
    const minutos = match[4]; // "00"

    // Obtener el año actual (puedes modificar esto si necesitas otro año)
    const anioActual = new Date().getFullYear();

    // Formar la fecha ISO: 2025-05-30T12:00
    return `${anioActual}-${mes}-${dia}T${hora}:${minutos}`;
  }

  return null;
}

