const datos = $input.all();
const dias = ['domingo', 'lunes', 'martes', 'mi√©rcoles', 'jueves', 'viernes', 's√°bado'];
const meses = ['enero', 'febrero', 'marzo', 'abril', 'mayo', 'junio', 'julio', 'agosto', 'septiembre', 'octubre', 'noviembre', 'diciembre'];

function formatearFecha(fecha, hora) {
  // Validar que fecha existe
  if (!fecha) {
    return 'Fecha no disponible';
  }

  // Combinar fecha y hora
  let fechaCompleta;
  if (hora) {
    // Si tenemos hora, combinarla con la fecha
    fechaCompleta = new Date(`${fecha}T${hora}`);
  } else {
    // Si no hay hora, usar solo la fecha
    fechaCompleta = new Date(fecha + 'T00:00:00');
  }

  // Validar que la fecha es v√°lida
  if (isNaN(fechaCompleta.getTime())) {
    return 'Fecha inv√°lida';
  }

  const diaSemana = dias[fechaCompleta.getDay()];
  const dia = fechaCompleta.getDate();
  const mes = meses[fechaCompleta.getMonth()];
  const anio = fechaCompleta.getFullYear();
  const horas = fechaCompleta.getHours();
  const minutos = fechaCompleta.getMinutes().toString().padStart(2, '0');

  return {
    fechaCompleta: `${capitalize(diaSemana)} ${dia} de ${mes} ${anio}`,
    hora: `${horas}:${minutos}`,
    diaSemana: capitalize(diaSemana),
    dia: dia,
    mes: mes
  };
}

function capitalize(str) {
  if (!str || typeof str !== 'string') {
    return '';
  }
  return str.charAt(0).toUpperCase() + str.slice(1);
}

const opciones = datos.map((item, index) => {
  // Pasar tanto la fecha como la hora a la funci√≥n
  const fechaData = formatearFecha(item.json.FECHA, item.json.HORA);

  return {
    rowId: item.json.Id ? item.json.Id.toString() : '', 
    title: `üö® ${fechaData.diaSemana} ${fechaData.dia} - ${fechaData.hora} hs`,
    description: `‚ùå CANCELAR ESTA CITA ‚Ä¢ ${capitalize(fechaData.mes)} ${fechaData.dia}`
  };
});

// Mensaje personalizado seg√∫n la cantidad de citas
const cantidadCitas = opciones.length;
let mensajePersonalizado = '';

if (cantidadCitas === 1) {
  mensajePersonalizado = 'Tienes 1 cita que puedes cancelar';
} else {
  mensajePersonalizado = `Tienes ${cantidadCitas} citas que puedes cancelar`;
}

const requestBody = {
  to: $('vars').first().json.numero,
  buttonText: "‚õî CANCELAR CITA",
  title: "üî¥ CANCELACI√ìN DE CITAS üî¥",
  description: `‚ö†Ô∏è ATENCI√ìN ‚ö†Ô∏è\n\n${mensajePersonalizado}\n\nüö® *IMPORTANTE:*\n‚ùå La cancelaci√≥n es PERMANENTE\n‚ùå No se puede deshacer\n‚ùå Deber√°s agendar nueva cita\n\n*Selecciona la cita a CANCELAR:*`,
  footer: "‚è∞ Cancela con 24hs de anticipaci√≥n",
  sections: [
    {
      title: "üî¥ CITAS PARA CANCELAR",
      rows: opciones
    }
  ]
};

// Agregar secci√≥n de advertencia si hay m√∫ltiples citas
if (cantidadCitas > 1) {
  requestBody.sections.push({
    title: "‚ö†Ô∏è ADVERTENCIA",
    rows: [{
      rowId: "warning",
      title: "‚ùó Cuidado al cancelar",
      description: "üî¥ Aseg√∫rate de cancelar la cita correcta"
    }]
  });
}

// Agregar secci√≥n de emergencia
requestBody.sections.push({
  title: "üìû CONTACTO DE EMERGENCIA",
  rows: [{
    rowId: "emergency",
    title: "üÜò ¬øNecesitas ayuda urgente?",
    description: "üì± WhatsApp: +54 11 1234-5678"
  }]
