const inputData = $input.first().json;

// Verificar si existe el campo messages.texto
if (!inputData.messages || !inputData.messages.texto) {
  return {
    ...inputData,
    url: '',
    urls: [],
    productoFormateado: null,
    textoLimpio: '',
    urlsFormateadas: ''
  };
}

const input = inputData.messages.texto;

// Regex para diferentes tipos de URLs
const markdownLinkRegex = /\[([^\]]*)\]\(([^)]+)\)/g;
const imageRegex = /!\[([^\]]*)\]\(([^)]+)\)/g;
const directUrlRegex = /(https?:\/\/[^\s<>"{}|\\^\[\]`]+)/g;

// Arrays para almacenar URLs y productos
const urls = [];
let cleanedMessage = input;

// Primero, extraer todas las URLs para evitar duplicados
const todasLasUrls = new Set();

// Extraer im√°genes markdown
let imageMatch;
while ((imageMatch = imageRegex.exec(input)) !== null) {
  todasLasUrls.add(imageMatch[2]);
  urls.push({
    type: 'image',
    text: imageMatch[1] || 'imagen',
    url: imageMatch[2],
    fullMatch: imageMatch[0]
  });
}

// Extraer enlaces markdown
let linkMatch;
while ((linkMatch = markdownLinkRegex.exec(input)) !== null) {
  if (!todasLasUrls.has(linkMatch[2])) {
    todasLasUrls.add(linkMatch[2]);
    urls.push({
      type: 'link',
      text: linkMatch[1] || 'enlace',
      url: linkMatch[2],
      fullMatch: linkMatch[0]
    });
  }
}

// Reemplazar todos los markdown links e im√°genes
urls.forEach(urlObj => {
  if (urlObj.fullMatch) {
    cleanedMessage = cleanedMessage.replace(urlObj.fullMatch, '');
  }
});

// Buscar URLs directas que no hayan sido capturadas antes
let directMatch;
const tempMessage = cleanedMessage;
while ((directMatch = directUrlRegex.exec(tempMessage)) !== null) {
  if (!todasLasUrls.has(directMatch[1])) {
    todasLasUrls.add(directMatch[1]);
    urls.push({
      type: 'direct',
      text: 'enlace',
      url: directMatch[1]
    });
  }
}

// Eliminar todas las URLs directas del mensaje
urls.forEach(urlObj => {
  if (urlObj.type === 'direct') {
    cleanedMessage = cleanedMessage.replace(urlObj.url, '');
  }
});

// Funci√≥n mejorada para extraer informaci√≥n del producto
function extraerInfoProducto(texto, urls) {
  const info = {
    nombre: '',
    precio: '',
    medidas: '',
    foto: ''
  };

  // Buscar precio con diferentes formatos
  const precioMatch = texto.match(/(?:precio|costo|valor|price)[\s:]*\$?\s*([\d,]+(?:\.\d{2})?)/i);
  if (precioMatch) {
    info.precio = '$' + precioMatch[1].replace(/[,\s]/g, '');
  }

  // Buscar medidas con diferentes formatos
  const medidasMatch = texto.match(/(?:medidas?|dimensiones?|tama√±o|size)[\s:]*([\d.,]+\s*x\s*[\d.,]+(?:\s*x\s*[\d.,]+)?(?:\s*(?:cm|m|mm|metros?|cent√≠metros?))?)/i);
  if (medidasMatch) {
    info.medidas = medidasMatch[1].trim();
  }

  // Extraer nombre del producto
  const lineas = texto.split('\n').map(l => l.trim()).filter(l => l);
  for (const linea of lineas) {
    // Buscar l√≠neas que parezcan ser el nombre del producto
    if (!linea.match(/precio|medidas|dimensiones|\$|https?:|costo|valor|size/i) && 
        linea.length > 3 && 
        !linea.match(/^\d+$/) && // No solo n√∫meros
        !linea.match(/^[\d.,]+\s*x\s*[\d.,]+/)) { // No medidas
      info.nombre = linea;
      break;
    }
  }

  // Buscar la primera imagen URL
  const imagenUrl = urls.find(u => u.type === 'image');
  if (imagenUrl) {
    info.foto = imagenUrl.url;
  }

  return info;
}

// Limpiar y formatear el texto para WhatsApp
function formatearParaWhatsApp(texto) {
  // Eliminar espacios m√∫ltiples y l√≠neas vac√≠as excesivas
  let textoFormateado = texto
    .split('\n')
    .map(linea => linea.trim())
    .filter(linea => linea.length > 0)
    .join('\n');

  // Eliminar caracteres especiales que puedan causar problemas
  textoFormateado = textoFormateado
    .replace(/\s{2,}/g, ' ') // M√∫ltiples espacios a uno solo
    .replace(/\n{3,}/g, '\n\n') // M√°ximo 2 saltos de l√≠nea consecutivos
    .replace(/^\s+|\s+$/g, '') // Trim general
    .replace(/[.,;:!?]+$/g, ''); // Eliminar puntuaci√≥n final innecesaria

  // Eliminar puntos finales en cada l√≠nea si no son necesarios
  textoFormateado = textoFormateado
    .split('\n')
    .map(linea => linea.replace(/\.$/, ''))
    .join('\n');

  return textoFormateado;
}

// Formatear URLs para WhatsApp
function formatearUrlsParaWhatsApp(urls) {
  if (urls.length === 0) return '';

  let urlsFormateadas = '\nüìé *Enlaces encontrados:*\n';

  urls.forEach((urlObj, index) => {
    if (urlObj.type === 'image') {
      urlsFormateadas += `\nüñºÔ∏è Imagen ${index + 1}:\n${urlObj.url}\n`;
    } else {
      urlsFormateadas += `\nüîó ${urlObj.text}:\n${urlObj.url}\n`;
    }
  });

  return urlsFormateadas;
}

// Extraer informaci√≥n del producto
const productoInfo = extraerInfoProducto(cleanedMessage, urls);

// Formatear texto limpio
const textoLimpio = formatearParaWhatsApp(cleanedMessage);

// Formatear URLs
const urlsFormateadas = formatearUrlsParaWhatsApp(urls);

// Formatear la salida del producto
let productoFormateado = '';
if (productoInfo.nombre || productoInfo.precio || productoInfo.medidas) {
  productoFormateado = 'üì¶ *INFORMACI√ìN DEL PRODUCTO*\n\n';
  productoFormateado += `*NOMBRE:* ${productoInfo.nombre || 'No especificado'}\n`;
  productoFormateado += `*PRECIO:* ${productoInfo.precio || 'No especificado'}\n`;
  productoFormateado += `*MEDIDAS:* ${productoInfo.medidas || 'No especificadas'}\n`;
  productoFormateado += `*FOTO:* ${productoInfo.foto || 'No disponible'}`;
}

// Crear mensaje completo para WhatsApp
const mensajeWhatsApp = textoLimpio + 
  (productoFormateado ? '\n\n' + productoFormateado : '') + 
  (urlsFormateadas ? '\n' + urlsFormateadas : '');

// Crear el objeto de salida
const output = {
  ...inputData,
  messages: {
    ...inputData.messages,
    texto: cleanedMessage,
    textoOriginal: inputData.messages.texto,
    textoLimpio: textoLimpio,
    mensajeWhatsApp: mensajeWhatsApp
  },
  url: urls.length > 0 ? urls[0].url : '',
  urls: urls,
