// OBTENER DATOS DEL INPUT
const editFields = $('Edit Fields').item.json;

// EXTRAER LOS 3 DATOS PRINCIPALES
const producto = editFields.producto || "Producto";
const precio = parseFloat(editFields.Precio || editFields.precio || 0);
const cantidad = parseInt(editFields.cantidad || 1);
const subtotal = precio * cantidad;

// FUNCIÓN PARA FORMATEAR MONEDA
function formatMoney(value) {
  return '$' + parseFloat(value).toLocaleString('es-AR');
}

// CONFIGURACIÓN DEL MENSAJE
const phoneNumber = "5492254423359";
const tipoAccion = "compra"; // Puede ser: compra, encargo, confirmacion, sin_stock

// CONFIGURAR MENSAJE SEGÚN TIPO
let msgConfig = {};

if (tipoAccion === "compra") {
  msgConfig = {
    title: "🔧 Techno Gas - Producto Disponible",
    text: "🎯 *" + producto + "*\n💰 Precio: " + formatMoney(precio) + "\n🔢 Cantidad: " + cantidad + "\n💵 Subtotal: " + formatMoney(subtotal) + "\n📦 Stock: ✅ disponible\n🚚 Envíos a Villa Gesell, Madariaga y Costa Esmeralda",
    footer: "¿Qué querés hacer?",
    btns: [
      { type: "quick_reply", id: "🛒 Comprar", text: "🛒 Comprar" },
      { type: "quick_reply", id: "other_product", text: " 📸 Ver imagen" }
    ]
  };
} else if (tipoAccion === "encargo") {
  const sena = Math.round(precio * 0.3);
  msgConfig = {
    title: "📋 Techno Gas - Encargo Disponible",
    text: "🎯 *" + producto + "*\n💰 Precio total: " + formatMoney(precio) + "\n🔢 Cantidad: " + cantidad + "\n💸 Seña (30%): " + formatMoney(sena) + "\n📅 Demora estimada: 7-10 días\n📦 Estado: A encargar\n🚚 Envíos a Villa Gesell, Madariaga y Costa Esmeralda",
    footer: "¿Querés encargarlo?",
    btns: [
      { type: "quick_reply", id: "order_now", text: "✅ Encargar" },
      { type: "quick_reply", id: "ask_more", text: "❓ Consultar" },
      { type: "quick_reply", id: "other_option", text: "🔍 Ver otros" }
    ]
  };
} else if (tipoAccion === "confirmacion") {
  msgConfig = {
    title: "🎉 Techno Gas - Confirmación",
    text: "✅ *Pedido confirmado*\n👤 Cliente: Cliente\n📦 Producto: " + producto + "\n🔢 Cantidad: " + cantidad + "\n💰 Total: " + formatMoney(subtotal) + "\n🏦 Alias: techno.gas.arg\n📱 Te contactamos para coordinar entrega",
    footer: "Próximos pasos:",
    btns: [
      { type: "quick_reply", id: "payment_done", text: "💳 Ya transferí" },
      { type: "quick_reply", id: "payment_later", text: "⏰ Pago después" },
      { type: "quick_reply", id: "need_help", text: "❓ Ayuda" }
    ]
  };
} else if (tipoAccion === "sin_stock") {
  msgConfig = {
    title: "❌ Techno Gas - Sin Stock",
    text: "🎯 *" + producto + "*\n💰 Precio: " + formatMoney(precio) + "\n🔢 Cantidad solicitada: " + cantidad + "\n📦 Stock: ❌ Sin stock\n📅 Reposición: Consultando con proveedor\n🔔 Te avisamos cuando llegue",
    footer: "¿Qué querés hacer?",
    btns: [
      { type: "quick_reply", id: "notify_arrival", text: "🔔 Avisarme" },
      { type: "quick_reply", id: "similar_product", text: "🔄 Ver similar" },
      { type: "quick_reply", id: "other_search", text: "🔍 Buscar otro" }
    ]
  };
} else {
  msgConfig = {
    title: "🔧 Techno Gas - Consulta",
    text: "Hola! Soy Lucy de Techno Gas.\n¿En qué puedo ayudarte hoy?\n\n🔧 Repuestos para gas\n🚚 Envíos a Villa Gesell, Madariaga y Costa Esmeralda\n📱 Atención personalizada",
    footer: "Elegí una opción:",
    btns: [
      { type: "quick_reply", id: "search_product", text: "🔍 Buscar repuesto" },
      { type: "quick_reply", id: "view_catalog", text: "📋 Ver catálogo" },
      { type: "quick_reply", id: "contact_support", text: "💬 Hablar con Lucy" }
    ]
  };
}

// CONSTRUIR PAYLOAD FINAL
const result = {
  to: phoneNumber,
  header: {
    title: msgConfig.title,
    hasMediaAttachment: false
  },
  text: msgConfig.text,
  footer: msgConfig.footer,
  buttons: msgConfig.btns
};

// RETORNAR RESULTADO
return [{ json: result }];
