const datos = $input.all();
const fechaCita = $input.first().json.FECHA; // ‚úÖ Fecha espec√≠fica del input
const horaCita = $input.first().json.HORA;   // ‚úÖ Hora espec√≠fica del input
const dias = ['domingo', 'lunes', 'martes', 'mi√©rcoles', 'jueves', 'viernes', 's√°bado'];
const meses = ['enero', 'febrero', 'marzo', 'abril', 'mayo', 'junio', 'julio', 'agosto', 'septiembre', 'octubre', 'noviembre', 'diciembre'];

function formatearFechaHora(fechaISO, hora) {
  // ‚úÖ Validaci√≥n para evitar errores
  if (!fechaISO || !hora) {
    return 'Fecha/Hora no disponible';
  }

  // ‚úÖ Limpiar y normalizar la hora
  let horaLimpia = hora.toString().trim();

  // Si la hora ya tiene segundos, usarla tal como est√°
  // Si no tiene segundos, agregarlos
  if (!horaLimpia.includes(':')) {
    horaLimpia = `${horaLimpia}:00:00`;
  } else if (horaLimpia.split(':').length === 2) {
    horaLimpia = `${horaLimpia}:00`;
  }

  // ‚úÖ Combinar fecha y hora correctamente
  const fechaCompleta = new Date(`${fechaISO}T${horaLimpia}`);

  // ‚úÖ Verificar si la fecha es v√°lida
  if (isNaN(fechaCompleta.getTime())) {
    // Debug: mostrar los valores que est√°n causando el problema
    console.log('Fecha ISO:', fechaISO);
    console.log('Hora original:', hora);
    console.log('Hora limpia:', horaLimpia);
    console.log('String completo:', `${fechaISO}T${horaLimpia}`);
    return 'Fecha inv√°lida';
  }

  const diaSemana = dias[fechaCompleta.getDay()];
  const dia = fechaCompleta.getDate();
  const mes = meses[fechaCompleta.getMonth()];
  const anio = fechaCompleta.getFullYear();
  const horas = fechaCompleta.getHours();
  const minutos = fechaCompleta.getMinutes().toString().padStart(2, '0');

  return {
    fechaCompleta: `${capitalize(diaSemana)} ${dia} de ${mes} ${anio}`,
    hora: `${horas}:${minutos}`,
    diaSemana: capitalize(diaSemana),
    dia: dia,
    mes: mes
  };
}

function capitalize(str) {
  // ‚úÖ Validaci√≥n para evitar el error
  if (!str || typeof str !== 'string') {
    return '';
  }
  return str.charAt(0).toUpperCase() + str.slice(1);
}

function getIconoAccion(index) {
  const iconos = ['üîÑ', '‚ôªÔ∏è', 'üîÉ', 'üîÅ', '‚Üª'];
  return iconos[index % iconos.length];
}

